                    isFineDelete({ message, userId, status }) && (
                      <MenuItem
                        onClick={() => {
                          if (disabled) { return; }
                          showRemove(true);
                          closeDropdown();
                        }}
                      >
                        {stringSet.CONTEXT_MENU_DROPDOWN__DELETE}
                      </MenuItem>
                    )
                  }
                </MenuItems>
              )}
            />
        }
      </div>
    </div>
  );
}
