interface Props {
  message: types.EveryMessage;
  userId: string;
  disabled: boolean;
  editDisabled: boolean;
  hasSeparator: boolean;
  channel: OpenChannel;
  renderCustomMessage?: types.RenderCustomMessage,
  deleteMessage(message: types.ClientUserMessage | types.ClientFileMessage, callback?: () => void): void;
  updateMessage(messageId: number, text: string, callback?: () => void): void;
  resendMessage(failedMessage: types.ClientUserMessage | types.ClientFileMessage): void;
  status?: string;
  chainTop: boolean;
  chainBottom: boolean;
}

export default function MessageHoc({
  message,
  userId,
  disabled,
  editDisabled,
  hasSeparator,
  channel,
  renderCustomMessage,
  deleteMessage,
  updateMessage,
  resendMessage,
  status,
  chainTop,
  chainBottom,
}: Props): ReactElement {
  let sender: SendBird.User = null;
  if (message.messageType !== 'admin') {
    sender = message.sender;
  }

  const { dateLocale } = useContext(LocalizationContext);

  const RenderedMessage = useMemo(() => {
    if (renderCustomMessage) {
      return renderCustomMessage(message, channel, chainTop, chainBottom);
    }
    return null;
  }, [message, renderCustomMessage]);

  const [showEdit, setShowEdit] = useState(false);
  const [showRemove, setShowRemove] = useState(false);
  const [showFileViewer, setShowFileViewer] = useState(false);
  const editMessageInputRef = useRef(null);

  let isByMe = false;

  if (sender && message.messageType !== 'admin') {
    // pending and failed messages are by me
    isByMe = (userId === sender.userId)
      || (message.requestState === SendingMessageStatus.PENDING)
      || (message.requestState === SendingMessageStatus.FAILED);
  }

  if(RenderedMessage) {
    return (
      <div className="sendbird-msg-hoc sendbird-msg--scroll-ref">
        <RenderedMessage message={message} />
      </div>
    );
  }

  if (message.messageType === 'user' && showEdit) {
    return (
      <MessageInput
        isEdit
        disabled={editDisabled}
        ref={editMessageInputRef}
        name={message.messageId}
        onSendMessage={updateMessage}
        onCancelEdit={() => { setShowEdit(false); }}
        value={message.message}
      />
    );
  }

  return (
    <div className="sendbird-msg-hoc sendbird-msg--scroll-ref">
      {/* date-separator */}
      {
        hasSeparator && (
          <DateSeparator>
            <Label type={LabelTypography.CAPTION_2} color={LabelColors.ONBACKGROUND_2}>
              {format(message.createdAt, 'MMMM dd, yyyy', { locale: dateLocale })}
            </Label>
          </DateSeparator>
        )
      }
      {/* Message */}
      {
        {
          [MessageTypes.ADMIN]: (() => {
            if (message.messageType === 'admin') {
              return (
                <OpenChannelAdminMessage message={message} />
              )
            }
          })(),
          [MessageTypes.FILE]: (() => {
            if (message.messageType === 'file') {
              return (
                <OpenChannelFileMessage
                  message={message}
                  disabled={disabled}
                  userId={userId}
                  showRemove={setShowRemove}
                  resendMessage={resendMessage}
                  status={status}
                  chainTop={chainTop}
                  chainBottom={chainBottom}
                />
              );
            }
            return;
          })(),
          [MessageTypes.OG]: (() => {
            if (message.messageType === 'user') {
              return (
                <OpenChannelOGMessage
                  message={message}
                  status={status}
                  userId={userId}
                  showEdit={setShowEdit}
                  disabled={disabled}
                  showRemove={setShowRemove}
                  resendMessage={resendMessage}
                  chainTop={chainTop}
                  chainBottom={chainBottom}
                />
              );
            }
            return;
          })(),
          [MessageTypes.THUMBNAIL]: (() => {
            if (message.messageType === 'file') {
              return (
                <OpenChannelThumbnailMessage
                  message={message}
                  disabled={disabled}
                  userId={userId}
                  showRemove={setShowRemove}
                  resendMessage={resendMessage}
                  onClick={setShowFileViewer}
                  status={status}
                  chainTop={chainTop}
                  chainBottom={chainBottom}
                />
              );
            }
            return;
          })(),
          [MessageTypes.USER]: (() => {
            if (message.messageType === 'user') {
              return (
                <OpenChannelUserMessage
                  message={message}
                  userId={userId}
                  disabled={disabled}
                  showEdit={setShowEdit}
                  showRemove={setShowRemove}
                  resendMessage={resendMessage}
                  status={status}
                  chainTop={chainTop}
                  chainBottom={chainBottom}
                />
              );
            }
            return;
          })(),
          [MessageTypes.UNKNOWN]: (() => {
            return;
            // return (
            //   <OpenChannelUnknownMessage message={message} />
            // );
          })(),
        }[getMessageType(message)]
      }
      {/* Modal */}
      {
        showRemove && (
          <RemoveMessageModal
            onCloseModal={() => setShowRemove(false)}
            onDeleteMessage={() => {
              if (message.messageType !== 'admin') {
                deleteMessage(message);
              }
            }}
          />
        )
      }
      {
        (showFileViewer && message.messageType === 'file') && (
          <FileViewer
            onClose={() => setShowFileViewer(false)}
            message={message}
            onDelete={() => deleteMessage(message)}
            isByMe={isByMe}
          />
        )
      }
      {/* {
        !((message.isFileMessage && message.isFileMessage()) || message.messageType === 'file')
        && !(message.isAdminMessage && message.isAdminMessage())
        && !(((message.isUserMessage && message.isUserMessage()) || message.messageType === 'user'))
        && !(showFileViewer)
        && (
          <UnknownMessage
            message={message}
            status={status}
            isByMe={isByMe}
            showRemove={setShowRemove}
            chainTop={chainTop}
            chainBottom={chainBottom}
          />
        )
      } */}
    </div>
  );
}
import React, { ReactElement, useRef, useState, useMemo } from 'react';
import isSameDay from 'date-fns/isSameDay';
import {
  ClientFileMessage,
  ClientUserMessage,
  EveryMessage,
  RenderCustomMessage,
} from '../../../index';
import MessageHoc from './MessageHOC';
import Icon, { IconTypes, IconColors } from '../../../ui/Icon';

import PlaceHolder, { PlaceHolderTypes } from '../../../ui/PlaceHolder';
import './openchannel-conversation-scroll.scss';
import { compareMessagesForGrouping } from '../utils';

interface Props {
  openchannel: SendBird.OpenChannel;
  user: SendBird.User;
  allMessages: Array<EveryMessage>;
  useMessageGrouping: boolean;
  isOnline: boolean;
  hasMore: boolean;
  renderCustomMessage?: RenderCustomMessage;
  onScroll(callback?: () => void): void;
  updateMessage(messageId: number, text: string, callback?: () => void): void;
  deleteMessage(message: ClientUserMessage | ClientFileMessage, callback?: () => void): void;
  resendMessage(failedMessage: ClientUserMessage | ClientFileMessage): void;
}

function OpenchannelConversationScroll(
  {
    useMessageGrouping = true,
    openchannel,
    user,
    allMessages,
    isOnline = true,
    hasMore,
    onScroll,
    renderCustomMessage,
    updateMessage,
    deleteMessage,
    resendMessage,
  }: Props,
  ref: React.RefObject<HTMLDivElement>,
): ReactElement {
  const scrollRef = ref || useRef(null);
  const [showScrollDownButton, setShowScrollDownButton] = useState(false);

  const handleOnScroll = (e) => {
    const element = e.target;
    const {
      scrollTop,
      scrollHeight,
      clientHeight,
    } = element;
    if (
      (scrollHeight > scrollTop + clientHeight)
      && (window.navigator.userAgent.indexOf('MSIE ') < 0) // don't show button in IE
    ) {
      setShowScrollDownButton(true);
    } else {
      setShowScrollDownButton(false);
    }

    if (!hasMore) {
      return;
    }
    if (scrollTop === 0) {
      const nodes = scrollRef.current.querySelectorAll('.sendbird-msg--scroll-ref');
      const first = nodes && nodes[0];
      onScroll(() => {
        try {
          first.scrollIntoView();
        } catch (error) { }
      });
    }
  };

  const scrollToBottom = () => {
    if (scrollRef && scrollRef.current) {
      scrollRef.current.scrollTo(0, scrollRef.current.scrollHeight);
      setShowScrollDownButton(false);
    }
  };

  const hasMessage = useMemo(() => {
    return allMessages.length > 0;
  }, [allMessages.length]);

  return (
    <div
      className="sendbird-openchannel-conversation-scroll"
      onScroll={handleOnScroll}
      ref={scrollRef}
    >
      <div className="sendbird-openchannel-conversation-scroll__container">
        <div className="sendbird-openchannel-conversation-scroll__container__padding" />
        <div className={`sendbird-openchannel-conversation-scroll__container__item-container${hasMessage ? '' : '--no-messages'}`}>
          {
            hasMessage
              ? (
                allMessages.map((message, index) => {
                  let status: string;

                  if (message.messageType !== 'admin') {
                    status = message.sendingStatus;
                  }

                  const previousMessage = allMessages[index - 1];
                  const nextMessage = allMessages[index - 1];

                  const previousMessageCreatedAt = previousMessage && previousMessage.createdAt;
                  const currentCreatedAt = message.createdAt;
                  // https://stackoverflow.com/a/41855608
                  const hasSeparator = !(previousMessageCreatedAt && (
                    isSameDay(currentCreatedAt, previousMessageCreatedAt)
                  ));

                  const [chainTop, chainBottom] = useMessageGrouping
                    ? compareMessagesForGrouping(previousMessage, message, nextMessage)
                    : [false, false];
                  return (
                    <MessageHoc
                      renderCustomMessage={renderCustomMessage}
                      channel={openchannel}
                      key={message.messageId}
                      message={message}
                      status={status}
                      userId={user.userId}
                      disabled={!isOnline}
                      editDisabled={openchannel.isFrozen}
                      hasSeparator={hasSeparator}
                      chainTop={chainTop}
                      chainBottom={chainBottom}
                      deleteMessage={deleteMessage}
                      updateMessage={updateMessage}
                      resendMessage={resendMessage}
                    />
                  )
                })
              )
              : (
                <PlaceHolder
                  className="sendbird-openchannel-conversation-scroll__container__place-holder"
                  type={PlaceHolderTypes.NO_MESSAGES}
                />
              )
          }
        </div>
        {
          showScrollDownButton && (
            <div
              className="sendbird-openchannel-conversation-scroll__container__scroll-bottom-button"
              onClick={scrollToBottom}
              onKeyDown={scrollToBottom}
              tabIndex={0}
              role="button"
            >
              <Icon
                width="24px"
                height="24px"
                type={IconTypes.CHEVRON_DOWN}
                fillColor={IconColors.CONTENT}
              />
            </div>
          )
        }
      </div>
    </div>
  );
}

export default React.forwardRef(OpenchannelConversationScroll);
export const SET_CURRENT_CHANNEL = 'SET_CURRENT_CHANNEL';
export const SET_CHANNEL_INVALID = 'SET_CHANNEL_INVALID';
export const RESET_MESSAGES = 'RESET_MESSAGES';
export const GET_PREV_MESSAGES_START = 'GET_PREV_MESSAGES_START';
export const GET_PREV_MESSAGES_SUCESS = 'GET_PREV_MESSAGES_SUCESS';
export const GET_PREV_MESSAGES_FAIL = 'GET_PREV_MESSAGES_FAIL';
export const SENDING_MESSAGE_FAILED = 'SENDING_MESSAGE_FAILED';
export const SENDING_MESSAGE_SUCCEEDED = 'SENDING_MESSAGE_SUCCEEDED';
export const SENDING_MESSAGE_START = 'SENDING_MESSAGE_START';
export const RESENDING_MESSAGE_START = 'RESENDING_MESSAGE_START';
export const FETCH_PARTICIPANT_LIST = 'FETCH_PARTICIPANT_LIST';
export const FETCH_BANNED_USER_LIST = 'FETCH_BANNED_USER_LIST';
export const FETCH_MUTED_USER_LIST= 'FETCH_MUTED_USER_LIST';
export const TRIM_MESSAGE_LIST= 'TRIM_MESSAGE_LIST';

// event handlers
export const ON_MESSAGE_RECEIVED = 'ON_MESSAGE_RECEIVED';
export const ON_MESSAGE_UPDATED = 'ON_MESSAGE_UPDATED';
export const ON_MESSAGE_DELETED = 'ON_MESSAGE_DELETED';
export const ON_MESSAGE_DELETED_BY_REQ_ID = 'ON_MESSAGE_DELETED_BY_REQ_ID';
export const ON_OPERATOR_UPDATED = 'ON_OPERATOR_UPDATED';
export const ON_USER_ENTERED = 'ON_USER_ENTERED';
export const ON_USER_EXITED = 'ON_USER_EXITED';
export const ON_USER_MUTED = 'ON_USER_MUTED';
export const ON_USER_UNMUTED = 'ON_USER_UNMUTED';
export const ON_USER_BANNED = 'ON_USER_BANNED';
export const ON_USER_UNBANNED = 'ON_USER_UNBANNED';
export const ON_CHANNEL_FROZEN = 'ON_CHANNEL_FROZEN';
export const ON_CHANNEL_UNFROZEN = 'ON_CHANNEL_UNFROZEN';
export const ON_CHANNEL_CHANGED = 'ON_CHANNEL_CHANGED';
export const ON_META_DATA_CREATED = 'ON_META_DATA_CREATED';
export const ON_META_DATA_UPDATED = 'ON_META_DATA_UPDATED';
export const ON_META_DATA_DELETED = 'ON_META_DATA_DELETED';
export const ON_META_COUNTERS_CREATED = 'ON_META_COUNTERS_CREATED';
export const ON_META_COUNTERS_UPDATED = 'ON_META_COUNTERS_UPDATED';
export const ON_META_COUNTERS_DELETED = 'ON_META_COUNTERS_DELETED';
export const ON_MENTION_RECEIVED = 'ON_MENTION_RECEIVED';
import { State as initialStateInterface } from './initialState';
import * as actionTypes from './actionTypes';

import compareIds from '../../../utils/compareIds.js';

/* eslint-disable  @typescript-eslint/no-explicit-any */
// @ts-ignore: Unreachable code error
interface ActionInterface {
  type: string;
  payload?: any;
}

export default function reducer(
  state: initialStateInterface,
  action: ActionInterface,
): initialStateInterface {
  switch (action.type) {
    case actionTypes.RESET_MESSAGES: {
      return {
        ...state,
        allMessages: [],
      };
    }

    case actionTypes.SET_CURRENT_CHANNEL: {
      const gottenChannel = action.payload;
      const operators = gottenChannel.operators;
      if (
        !state.isInvalid
        && state.currentOpenChannel
        && state.currentOpenChannel.url
        && (state.currentOpenChannel.url === gottenChannel.url)
      ) {
        return state;
      }
      return {
        ...state,
        currentOpenChannel: gottenChannel,
        isInvalid: false,
        operators: operators,
        participants: operators,
        bannedParticipantIds: [],
        mutedParticipantIds: [],
      };
    }
    case actionTypes.SET_CHANNEL_INVALID: {
      return {
        ...state,
        isInvalid: true,
      };
    }
    case actionTypes.GET_PREV_MESSAGES_START: {
      return {
        ...state,
        loading: true,
      };
    }
    case actionTypes.GET_PREV_MESSAGES_SUCESS:
    case actionTypes.GET_PREV_MESSAGES_FAIL: {
      const isFailed = (action.type === actionTypes.GET_PREV_MESSAGES_FAIL);
      const {
        currentOpenChannel = {},
        messages = [],
        hasMore,
        lastMessageTimestamp,
      } = action.payload;
      const actionChannelUrl = currentOpenChannel.url;
      const receivedMessages = isFailed ? [] : messages;
      const _hasMore = isFailed ? false : hasMore;
      const _lastMessageTimestamp = isFailed ? 0 : lastMessageTimestamp;

      const stateChannel = state.currentOpenChannel;
      const stateChannelUrl = stateChannel.url;

      if (actionChannelUrl !== stateChannelUrl) {
        return state;
      }

      const filteredAllMessages = state.allMessages.filter((message) => (
        !(receivedMessages.find(({ messageId }) => compareIds(messageId, message.messageId)))
      ));
      return {
        ...state,
        loading: false,
        initialized: true,
        hasMore: _hasMore,
        lastMessageTimestamp: _lastMessageTimestamp,
        allMessages: [
          ...receivedMessages,
          ...filteredAllMessages,
        ],
      };
    }
    case actionTypes.SENDING_MESSAGE_START: {
      const {
        message,
        channel,
      } = action.payload;
      if (channel.url !== state.currentOpenChannel.url) {
        return state;
      }
      return {
        ...state,
        allMessages: [
          ...state.allMessages,
          { ...message },
        ],
      };
    }
    case actionTypes.SENDING_MESSAGE_SUCCEEDED: {
      const sentMessage = action.payload;
      const newMessages = state.allMessages.map((m) => (
        compareIds(m.reqId, sentMessage.reqId) ? sentMessage : m
      ));
      return {
        ...state,
        allMessages: newMessages,
      };
    }
    case actionTypes.SENDING_MESSAGE_FAILED: {
      const sentMessage = action.payload;
      return {
        ...state,
        allMessages: state.allMessages.map((m) => (
          compareIds(m.reqId, sentMessage.reqId) ? sentMessage : m
        )),
      };
    }
    case actionTypes.TRIM_MESSAGE_LIST: {
      const { allMessages } = state;
      const experimentalMessageLimit = action.payload?.experimentalMessageLimit;
      if (experimentalMessageLimit
        && experimentalMessageLimit > 0
        && allMessages?.length > experimentalMessageLimit
      ) {
        const sliceAt = allMessages.length - experimentalMessageLimit;
        return {
          ...state,
          allMessages: allMessages.slice(sliceAt),
        }
      }
      return state;
    }
    case actionTypes.RESENDING_MESSAGE_START: {
      const eventedChannel = action.payload.channel;
      const resentMessage = action.payload.message;
      if (eventedChannel.url !== state.currentOpenChannel.url) {
        return state;
      }
      return {
        ...state,
        allMessages: state.allMessages.map((m) => (
          compareIds(m.reqId, resentMessage.reqId) ? resentMessage : m
        )),
      };
    }
    case actionTypes.FETCH_PARTICIPANT_LIST: {
      const eventedChannel = action.payload.channel;
      const fetchedParticipantList = action.payload.users;
      if (eventedChannel.url !== state.currentOpenChannel.url) {
        return state;
      }
      return {
        ...state,
        participants: [... state.participants, ...fetchedParticipantList],
        // Should check duplication
      };
    }
    case actionTypes.FETCH_BANNED_USER_LIST: {
      const eventedChannel = action.payload.channel;
      const fetchedBannedUserList = action.payload.users;
      if (
        (eventedChannel.url !== state.currentOpenChannel.url)
        || !(fetchedBannedUserList.every(user => typeof user.userId === 'string'))
      ) {
        return state;
      }
      return {
        ...state,
        bannedParticipantIds: [
          ... state.bannedParticipantIds,
          ...fetchedBannedUserList.map(user => user.userId),
        ],
        // Should check duplication
      };
    }
    case actionTypes.FETCH_MUTED_USER_LIST: {
      const eventedChannel = action.payload.channel;
      const fetchedMutedUserList = action.payload.users;
      if (
        (eventedChannel.url !== state.currentOpenChannel.url)
        || !(fetchedMutedUserList.every(user => typeof user.userId === 'string'))
      ) {
        return state;
      }
      return {
        ...state,
        mutedParticipantIds: [
          ... state.bannedParticipantIds,
          ...fetchedMutedUserList.map(user => user.userId),
        ],
        // Should check duplication
      };
    }
    // events
    case actionTypes.ON_MESSAGE_RECEIVED: {
      const eventedChannel = action.payload.channel;
      const receivedMessage = action.payload.message;

      const {
        currentOpenChannel
      } = state;

      if (
        !compareIds(eventedChannel.url, currentOpenChannel.url)
        || (
          !(state.allMessages.map(
            (message) => message.messageId).indexOf(receivedMessage.messageId) < 0
        ))
      ) {
        return state;
      }

      return {
        ...state,
        allMessages: [...state.allMessages, receivedMessage],
      };
    }
    case actionTypes.ON_MESSAGE_UPDATED: {
      const eventedChannel = action.payload.channel;
      const updatedMessage = action.payload.message;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== eventedChannel.url)) {
        return state;
      }
      return {
        ...state,
        allMessages: state.allMessages.map((message) => (
          message.isIdentical(updatedMessage)
            ? updatedMessage
            : message
        )),
      };
    }
    case actionTypes.ON_MESSAGE_DELETED: {
      const eventedChannel = action.payload.channel;
      const deletedMessageId = action.payload.messageId;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== eventedChannel.url)) {
        return state;
      }
      return {
        ...state,
        allMessages: state.allMessages.filter((message) => (
          !compareIds(message.messageId, deletedMessageId)
        )),
      };
    }
    case actionTypes.ON_MESSAGE_DELETED_BY_REQ_ID: {
      return {
        ...state,
        allMessages: state.allMessages.filter((m) => (
          !compareIds(m.reqId, action.payload)
        )),
      };
    }
    case actionTypes.ON_OPERATOR_UPDATED: {
      const eventedChannel = action.payload.channel;
      const updatedOperators = action.payload.operators;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== eventedChannel.url)) {
        return state;
      }
      return {
        ...state,
        currentOpenChannel: {
          ...state.currentOpenChannel,
          operators: updatedOperators,
        },
        operators: updatedOperators,
      };
    }
    case actionTypes.ON_USER_ENTERED: {
      const eventedChannel = action.payload.channel;
      const enteredUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== eventedChannel.url)) {
        return state;
      }
      return {
        ...state,
        participants: [...state.participants, enteredUser],
      };
    }
    case actionTypes.ON_USER_EXITED: {
      const eventedChannel = action.payload.channel;
      const exitedUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== eventedChannel.url)) {
        return state;
      }
      return {
        ...state,
        participants: state.participants.filter((participant) => (
          !compareIds(participant.userId, exitedUser.userId)
        )),
      };
    }
    case actionTypes.ON_USER_MUTED: {
      const eventedChannel = action.payload.channel;
      const mutedUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (
        !currentChannel
        || (currentChannel.url && (currentChannel.url !== eventedChannel.url))
        || state.mutedParticipantIds.indexOf(mutedUser.userId) >= 0
      ) {
        return state;
      }
      return {
        ...state,
        mutedParticipantIds: [...state.mutedParticipantIds, mutedUser.userId],
      };
    }
    case actionTypes.ON_USER_UNMUTED: {
      const eventedChannel = action.payload.channel;
      const unmutedUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (
        !currentChannel
        || (currentChannel.url && (currentChannel.url !== eventedChannel.url))
        || state.mutedParticipantIds.indexOf(unmutedUser.userId) < 0
      ) {
        return state;
      }
      return {
        ...state,
        mutedParticipantIds: state.mutedParticipantIds.filter(userId => userId !== unmutedUser.userId),
      };
    }
    case actionTypes.ON_USER_BANNED: {
      const eventedChannel = action.payload.channel;
      const bannedUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (
        !currentChannel
        || (currentChannel.url && (currentChannel.url !== eventedChannel.url))
        || state.bannedParticipantIds.indexOf(bannedUser.userId) >= 0
      ) {
        return state;
      }
      return {
        ...state,
        bannedParticipantIds: [...state.bannedParticipantIds, bannedUser.userId],
      };
    }
    case actionTypes.ON_USER_UNBANNED: {
      const eventedChannel = action.payload.channel;
      const unbannedUser = action.payload.user;
      const currentChannel = state.currentOpenChannel;
      if (
        !currentChannel
        || (currentChannel.url && (currentChannel.url !== eventedChannel.url))
        || state.bannedParticipantIds.indexOf(unbannedUser.userId) < 0
      ) {
        return state;
      }
      return {
        ...state,
        bannedParticipantIds: state.bannedParticipantIds.filter(userId => userId !== unbannedUser.userId),
      };
    }
    case actionTypes.ON_CHANNEL_FROZEN: {
      const frozenChannel = action.payload;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== frozenChannel.url)) {
        return state;
      }
      return {
        ...state,
        frozen: true,
      };
    }
    case actionTypes.ON_CHANNEL_UNFROZEN: {
      const unfrozenChannel = action.payload;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== unfrozenChannel.url)) {
        return state;
      }
      return {
        ...state,
        frozen: false,
      };
    }
    case actionTypes.ON_CHANNEL_CHANGED: {
      const changedChannel = action.payload;
      const currentChannel = state.currentOpenChannel;
      if (!currentChannel || currentChannel.url && (currentChannel.url !== changedChannel.url)) {
        return state;
      }
      return {
        ...state,
        currentOpenChannel: changedChannel,
