                (users) => {
                  messagesDispatcher({
                    type: messageActionTypes.FETCH_MUTED_USER_LIST,
                    payload: {
                      channel: openChannel,
                      users,
                    },
                  });
                },
              );
            }
          });
        } else {
          logger.warning('OpenChannel | useSetChannel fetching channel failed', { channelUrl, error });
          messagesDispatcher({
            type: messageActionTypes.SET_CHANNEL_INVALID,
            payload: null,
          });
        }
      });
      // .then((openChannel) => {
