}
interface StaticParams {
  sdk: SendbirdUIKit.Sdk;
  logger: SendbirdUIKit.Logger;
  messagesDispatcher: ({ type: string, payload: any }) => void;
}

function useSendMessageCallback(
  { currentOpenChannel, onBeforeSendUserMessage, checkScrollBottom, messageInputRef }: DynamicParams,
  { sdk, logger, messagesDispatcher }: StaticParams,
): () => void {
  return useCallback(() => {
    if (sdk && sdk.UserMessageParams) {
      const text = messageInputRef.current.value;
      const createParamsDefault = (txt: string | number): Sendbird.UserMessageParams => {
        const message = typeof txt === 'string' ? txt.trim() : txt.toString(10).trim();
        const params = new sdk.UserMessageParams();
        params.message = message;
        return params;
      }
      const createCustomParams = onBeforeSendUserMessage && typeof onBeforeSendUserMessage === 'function';
      if (createCustomParams) {
        logger.info('OpenChannel | useSendMessageCallback: Creating params using onBeforeSendUserMessage', onBeforeSendUserMessage);
      }
      const params = onBeforeSendUserMessage ? onBeforeSendUserMessage(text) : createParamsDefault(text);
      logger.info('OpenChannel | useSendMessageCallback: Sending message has started', params);

      const isBottom = checkScrollBottom();
      const pendingMessage = currentOpenChannel.sendUserMessage(params, (message, error) => {
        if (!error) {
          logger.info('OpenChannel | useSendMessageCallback: Sending message succeeded', message);
          messagesDispatcher({
            type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
            payload: message,
          });
          if (isBottom) {
            setTimeout(() => {
              utils.scrollIntoLast();
            });
          }
        } else {
          logger.warning('OpenChannel | useSendMessageCallback: Sending message failed', error);
          messagesDispatcher({
            type: messageActionTypes.SENDING_MESSAGE_FAILED,
            payload: messageActionTypes,
          });
        }
      });
      messagesDispatcher({
        type: messageActionTypes.SENDING_MESSAGE_START,
        payload: {
          message: pendingMessage,
          channel: currentOpenChannel,
        }
      });
    }
  }, [currentOpenChannel, onBeforeSendUserMessage, checkScrollBottom, messageInputRef]);
}

export default useSendMessageCallback;
import Sendbird from 'sendbird';
import { useCallback } from 'react';
import * as messageActionTypes from '../dux/actionTypes';
import * as utils from '../utils';

interface DynamicParams {
  currentOpenChannel: SendbirdUIKit.OpenChannelType;
  onBeforeSendFileMessage: (file: File) => Sendbird.FileMessageParams;
  checkScrollBottom: () => boolean;
  imageCompression?: {
    compressionRate?: number,
    resizingWidth?: number | string,
    resizingHeight?: number | string,
  };
}
interface StaticParams {
  sdk: SendbirdUIKit.Sdk;
  logger: SendbirdUIKit.Logger;
  messagesDispatcher: ({ type: string, payload: any }) => void;
}

type CallbackReturn = (file: File) => void;

function useFileUploadCallback(
  {
    currentOpenChannel,
    checkScrollBottom,
    imageCompression = {},
    onBeforeSendFileMessage,
  }: DynamicParams,
  { sdk, logger, messagesDispatcher }: StaticParams,
): CallbackReturn {
  return useCallback((file) => {
    if (sdk && sdk.FileMessageParams) {
      const {
        compressionRate,
        resizingWidth,
        resizingHeight,
      } = imageCompression;
      const createCustomParams = onBeforeSendFileMessage && typeof onBeforeSendFileMessage === 'function';

      const compressibleFileType = (file.type === 'image/jpg' || file.type === 'image/png' || file.type === 'image/jpeg');
      const compressibleRatio = (compressionRate > 0) && (compressionRate < 1);
      // pxToNumber returns null if values are invalid
      const compressibleDiamensions = utils.pxToNumber(resizingWidth)
        || utils.pxToNumber(resizingHeight);

      const canCompressImage = compressibleFileType && (compressibleRatio || compressibleDiamensions);

      const createParamsDefault = (file_): Sendbird.FileMessageParams => {
        const params = new sdk.FileMessageParams();
        params.file = file_;
        return params;
      };

      if (canCompressImage) { // Using image compression
        try {
          const image: HTMLImageElement = document.createElement('img');
          image.src = URL.createObjectURL(file);
          image.onload = () => {
            URL.revokeObjectURL(image.src);
            const canvas = document.createElement('canvas');
            const imageWidth = image.naturalWidth || image.width;
            const imageHeight = image.naturalHeight || image.height;
            let targetWidth = utils.pxToNumber(resizingWidth) || imageWidth;
            let targetHeight = utils.pxToNumber(resizingHeight) || imageHeight;

            // In canvas.toBlob(callback, mimeType, qualityArgument)
            // qualityArgument doesnt work
            // so in case compressibleDiamensions are not present, we use ratio
            if (file.type === 'image/png' && !compressibleDiamensions) {
              targetWidth *= compressionRate;
              targetHeight *= compressionRate;
            }

            canvas.width = targetWidth;
            canvas.height = targetHeight;
            const context = canvas.getContext('2d');
            context.drawImage(image, 0, 0, targetWidth, targetHeight);
            context.canvas.toBlob(
              (newImageBlob) => {
                const compressedFile = new File([newImageBlob], file.name, { type: file.type });
                if (createCustomParams) {
                  logger.info('OpenChannel | useFileUploadCallback: Creating params using onBeforeSendFileMessage', onBeforeSendFileMessage);
                }
                const params = onBeforeSendFileMessage ? onBeforeSendFileMessage(compressedFile) : createParamsDefault(compressedFile);
                logger.info('OpenChannel | useFileUploadCallback: Uploading file message start', params);

                const isBottom = checkScrollBottom();
                const pendingMessage = currentOpenChannel.sendFileMessage(
                  params,
                  (message: SendbirdUIKit.ClientFileMessage, error) => {
                    if (!error) {
                      logger.info('OpenChannel | useFileUploadCallback: Sending message succeeded', message);
                      messagesDispatcher({
                        type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
                        payload: message,
                      });
                      if (isBottom) {
                        setTimeout(() => {
                          utils.scrollIntoLast();
                        });
                      }
                    } else {
                      logger.error('OpenChannel | useFileUploadCallback: Sending file message failed', { message, error });
                      message.localUrl = URL.createObjectURL(file);
                      message.file = file;
                      messagesDispatcher({
                        type: messageActionTypes.SENDING_MESSAGE_FAILED,
                        payload: message,
                      });
                    }
                  },
                );

                messagesDispatcher({
                  type: messageActionTypes.SENDING_MESSAGE_START,
                  payload: {
                    message: {
                      ...pendingMessage,
                      url: URL.createObjectURL(file),
                      // pending thumbnail message seems to be failed
                      requestState: 'pending',
                    },
                    channel: currentOpenChannel,
                  }
                });
              },
              file.type,
              compressionRate,
            );
          }
        } catch (error) {
          logger.warning('OpenChannel | useFileUploadCallback: Sending file message with image compression failed', error);
        }
      } else { // Not using image compression
        if (createCustomParams) {
          logger.info('OpenChannel | useFileUploadCallback: Creating params using onBeforeSendFileMessage', onBeforeSendFileMessage);
        }
        const params = onBeforeSendFileMessage ? onBeforeSendFileMessage(file) : createParamsDefault(file);
        logger.info('OpenChannel | useFileUploadCallback: Uploading file message start', params);

        const isBottom = checkScrollBottom();
        const pendingMessage = currentOpenChannel.sendFileMessage(
          params,
          (message: SendbirdUIKit.ClientFileMessage, error) => {
            if (!error) {
              logger.info('OpenChannel | useFileUploadCallback: Sending message succeeded', message);
              messagesDispatcher({
                type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
                payload: message,
              });
              if (isBottom) {
                setTimeout(() => {
                  utils.scrollIntoLast();
                });
              }
            } else {
              logger.error('OpenChannel | useFileUploadCallback: Sending file message failed', { message, error });
              message.localUrl = URL.createObjectURL(file);
              message.file = file;
              messagesDispatcher({
                type: messageActionTypes.SENDING_MESSAGE_FAILED,
                payload: message,
              });
            }
          }
        );

        messagesDispatcher({
          type: messageActionTypes.SENDING_MESSAGE_START,
          payload: {
            message: {
              ...pendingMessage,
              url: URL.createObjectURL(file),
              // pending thumbnail message seems to be failed
              requestState: 'pending',
            },
            channel: currentOpenChannel,
          }
        });
      }
    }
  }, [currentOpenChannel, onBeforeSendFileMessage, checkScrollBottom, imageCompression]);
}

export default useFileUploadCallback;
import Sendbird from 'sendbird';
import { useCallback } from 'react';
import * as messageActionTypes from '../dux/actionTypes';

interface DynamicParams {
  currentOpenChannel: SendbirdUIKit.OpenChannelType;
  onBeforeSendUserMessage?: (text) => Sendbird.UserMessageParams;
}
interface StaticParams {
  sdk: SendbirdUIKit.Sdk;
  logger: SendbirdUIKit.Logger;
  messagesDispatcher: ({ type: string, payload :any }) => void;
}
type CallbackReturn = (messageId, text, callback) => void;

function useUpdateMessageCallback(
  { currentOpenChannel, onBeforeSendUserMessage }: DynamicParams,
  { sdk, logger, messagesDispatcher }: StaticParams,
): CallbackReturn {
  return useCallback((messageId, text, callback) => {
    const createParamsDefault = (txt) => {
      const params = new sdk.UserMessageParams();
      params.message = txt;
      return params;
    };

    if (onBeforeSendUserMessage && typeof onBeforeSendUserMessage === 'function') {
      logger.info('OpenChannel | useUpdateMessageCallback: Creating params using onBeforeUpdateUserMessage');
    }
    const params = onBeforeSendUserMessage ? onBeforeSendUserMessage(text) : createParamsDefault(text);
    currentOpenChannel.updateUserMessage(messageId, params, (message, error) => {
      if (callback) {
        callback();
      }
      if (!error) {
        logger.info('OpenChannel | useUpdateMessageCallback: Updating message succeeded', { message, params });
        messagesDispatcher({
          type: messageActionTypes.ON_MESSAGE_UPDATED,
          payload: {
            channel: currentOpenChannel,
            message,
          },
        });
      } else {
        logger.warning('OpenChannel | useUpdateMessageCallback: Updating message failed', error);
      }
    });
  }, [currentOpenChannel, onBeforeSendUserMessage]);
}

export default useUpdateMessageCallback;
import { useCallback } from 'react';
import * as messageActionTypes from '../dux/actionTypes';

interface DynamicParams {
  currentOpenChannel: SendbirdUIKit.OpenChannelType;
}
interface StaticParams {
  logger: SendbirdUIKit.Logger;
  messagesDispatcher: ({ type: string, payload: any }) => void;
}

type CallbackReturn = (
  message: SendbirdUIKit.ClientUserMessage | SendbirdUIKit.ClientFileMessage,
  callback?: () => void,
) => void;

function useDeleteMessageCallback(
  { currentOpenChannel }: DynamicParams,
  { logger, messagesDispatcher }: StaticParams,
): CallbackReturn {
  return useCallback((message, callback) => {
    logger.info('OpenChannel | useDeleteMessageCallback: Deleting message', message);
    const { sendingStatus } = message;
    logger.info('OpenChannel | useDeleteMessageCallback: Deleting message requestState', sendingStatus);

    if (sendingStatus === 'failed' || sendingStatus === 'pending') {
      logger.info('OpenChannel | useDeleteMessageCallback: Deleted message from local', message);
      messagesDispatcher({
        type: messageActionTypes.ON_MESSAGE_DELETED_BY_REQ_ID,
        payload: message.reqId,
      });
      if (callback) {
        callback();
      }
    } else {
      if (!(message.messageType ==='file' || message.messageType === 'user')) {
        return;
      }
      const messageToDelete = message as SendBird.UserMessage;
      currentOpenChannel.deleteMessage(messageToDelete, (error) => {
        logger.info('OpenChannel | useDeleteMessageCallback: Deleting message on server', sendingStatus);
        if (callback) {
          callback();
        }
        if (!error) {
          logger.info('OpenChannel | useDeleteMessageCallback: Deleting message succeeded', message);
          messagesDispatcher({
            type: messageActionTypes.ON_MESSAGE_DELETED,
            payload: {
              channel: currentOpenChannel,
              messageId: message.messageId,
            },
          });
        } else {
          logger.warning('OpenChannel | useDeleteMessageCallback: Deleting message failed', error);
        }
      });
    }
  }, [currentOpenChannel]);
}

export default useDeleteMessageCallback;
import { useCallback } from 'react';
import * as messageActionTypes from '../dux/actionTypes';

interface DynamicParams {
  currentOpenChannel: SendbirdUIKit.OpenChannelType;
}
interface StaticParams {
  logger: SendbirdUIKit.Logger;
  messagesDispatcher: ({ type: string, payload: any }) => void;
}
type CallbackReturn = (failedMessage: SendbirdUIKit.ClientUserMessage | SendbirdUIKit.ClientFileMessage) => void;

function useResendMessageCallback(
  { currentOpenChannel }: DynamicParams,
  { logger, messagesDispatcher }: StaticParams,
): CallbackReturn {
  return useCallback((failedMessage) => {
    logger.info('OpenChannel | useResendMessageCallback: Resending message has started', failedMessage);
    const { messageType, file } = failedMessage;
    if (failedMessage && typeof failedMessage.isResendable === 'function' && failedMessage.isResendable()) {
      // eslint-disable-next-line no-param-reassign
      failedMessage.requestState = 'pending';
      messagesDispatcher({
        type: messageActionTypes.RESENDING_MESSAGE_START,
        payload: {
          channel: currentOpenChannel,
          message: failedMessage,
        },
      });

      // userMessage
      if (messageType === 'user' && failedMessage.messageType === 'user') {
        currentOpenChannel.resendUserMessage(failedMessage, (message, error) => {
          if (!error) {
            logger.info('OpenChannel | useResendMessageCallback: Reseding message succeeded', message);
            messagesDispatcher({
              type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
              payload: message,
            });
          } else {
            logger.warning('OpenChannel | useResendMessageCallback: Resending message failed', error);
            // eslint-disable-next-line no-param-reassign
            failedMessage.requestState = 'failed';
            messagesDispatcher({
              type: messageActionTypes.SENDING_MESSAGE_FAILED,
              payload: failedMessage,
            });
          }
        });
        return;
      }

      // fileMessage
      if (messageType === 'file' && failedMessage.messageType === 'file') {
        currentOpenChannel.resendFileMessage(failedMessage, file, (message, error) => {
          if (!error) {
            logger.info('OpenChannel | useResendMessageCallback: Resending file message succeeded', message);
            messagesDispatcher({
              type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
              payload: message,
            });
          } else {
            logger.warning('OpenChannel | useResendMessageCallback: Resending file message failed', error);
            // eslint-disable-next-line no-param-reassign
            failedMessage.requestState = 'failed';
            messagesDispatcher({
              type: messageActionTypes.SENDING_MESSAGE_FAILED,
              payload: failedMessage,
            });
          }
        });
      }
    } else {
      // to alert user on console
      // eslint-disable-next-line no-console
      console.error('OpenChannel | useResendMessageCallback: Message is not resendable');
      logger.warning('OpenChannel | useResendMessageCallback: Message is not resendable', failedMessage);
    }
  }, [currentOpenChannel]);
}

export default useResendMessageCallback;
import { useEffect, useState } from 'react';
import * as messageActionTypes from '../dux/actionTypes';

interface DynamicParams {
  messagesLength: number;
  experimentalMessageLimit: number;
}

interface StaticParams {
  messagesDispatcher: ({
    type: string,
    payload: { experimentalMessageLimit: number }
  }) => void;
  logger: SendbirdUIKit.Logger;
}

const THROTTLE_TIMER = 5000;

// to trim message list so that we wont keep thousands of messages in memory
// We are throttling here; not debouncing
// it will be called once very 5 sec if messagesLength, experimentalMessageLimit changes
// we check if messagesLength > experimentalMessageLimit before dispatching action
function useTrimMessageList(
  { messagesLength, experimentalMessageLimit }: DynamicParams,
  { messagesDispatcher, logger }: StaticParams,
): void {
  const [inProgress, setInProgress] = useState(false);
  useEffect(() => {
    if (inProgress) {
      return;
    }
    if (typeof messagesLength === 'number' && messagesLength > experimentalMessageLimit) {
      logger.info('Trimming MessageList');
      messagesDispatcher({
        type: messageActionTypes.TRIM_MESSAGE_LIST,
        payload: { experimentalMessageLimit },
      });
    }
    setInProgress(true);
    setTimeout(() => { setInProgress(false); }, THROTTLE_TIMER);
  }, [messagesLength, experimentalMessageLimit]);
}

export default useTrimMessageList;
import React, {
  useRef,
  useEffect,
  useReducer,
  useContext,
  useMemo,
} from 'react';
import * as utils from './utils';
import './index.scss';

import MessageInputWrapper from './components/MessageInpuetWrapper';
import FrozenChannelNotification from './components/FrozenNotification';
import OpenchannelConversationHeader from '../../ui/OpenchannelConversationHeader';
import OpenchannelConversationScroll from './components/OpenchannelConversationScroll';
import PlaceHolder, { PlaceHolderTypes } from '../../ui/PlaceHolder';
import { UserProfileProvider } from '../../lib/UserProfileContext';

import { OpenChannelProps } from '../../index';
import messagesReducer from './dux/reducers';
import messagesInitialState from './dux/initialState';
import * as messageActionTypes from './dux/actionTypes';
import withSendbirdContext from '../../lib/SendbirdSdkContext.jsx';
import { LocalizationContext } from '../../lib/LocalizationContext';
import { scrollIntoLast } from './utils';
import * as topics from '../../lib/pubSub/topics';

// hooks
import useSetChannel from './hooks/useSetChannel';
import useHandleChannelEvents from './hooks/useHandleChannelEvents';
import useInitialMessagesFetch from './hooks/useInitialMessagesFetch';
import useScrollCallback from './hooks/useScrollCallback';
import useCheckScrollBottom from './hooks/useCheckScrollBottom';
import useSendMessageCallback from './hooks/useSendMessageCallback';
import useFileUploadCallback from './hooks/useFileUploadCallback';
import useUpdateMessageCallback from './hooks/useUpdateMessageCallback';
import useDeleteMessageCallback from './hooks/useDeleteMessageCallback';
import useResendMessageCallback from './hooks/useResendMessageCallback';
import useTrimMessageList from './hooks/useTrimMessageList';

const COMPONENT_CLASS_NAME = 'sendbird-openchannel-conversation';

interface Props extends OpenChannelProps {
  stores: {
    sdkStore?: SendbirdUIKit.SdkStore,
    userStore?: SendbirdUIKit.UserStore,
  };
  config: {
    userId: string,
    isOnline: boolean,
    logger?: SendbirdUIKit.Logger,
    theme?: string,
    /* eslint-disable @typescript-eslint/no-explicit-any*/
    pubSub: any,
    disableUserProfile?: boolean,
    renderUserProfile?(): JSX.Element,
    imageCompression?: {
      compressionRate?: number,
      resizingWidth?: number | string,
      resizingHeight?: number | string,
    },
  };
}

export const OpenchannelConversation = (props: Props): JSX.Element => {
  const {
    // internal props
    stores,
    config,
    // normal props
    useMessageGrouping,
    channelUrl,
    queries = {},
    disableUserProfile,
    fetchingParticipants = false, // We didn't decide to support fetching participant list
    renderCustomMessage,
    renderUserProfile,
    renderChannelTitle,
    renderMessageInput,
    onBeforeSendUserMessage,
    experimentalMessageLimit,
    onBeforeSendFileMessage,
    onChatHeaderActionClick
  } = props;

  const {
    sdkStore,
    userStore,
  } = stores;
  const {
    userId,
    isOnline,
    logger,
    pubSub,
    imageCompression,
  } = config;

  const {
    sdk
  } = sdkStore;
  const {
    user
  } = userStore;

  // hook variables
  const [messagesStore, messagesDispatcher] = useReducer(messagesReducer, messagesInitialState);
  const {
    allMessages,
    loading,
    initialized,
    currentOpenChannel,
    isInvalid,
    hasMore,
    lastMessageTimestamp,
    operators,
    bannedParticipantIds,
    mutedParticipantIds,
  } = messagesStore;
  // ref
  const messageInputRef = useRef(null); // useSendMessageCallback
  const conversationScrollRef = useRef(null); // useScrollAfterSendMessageCallback
  const { stringSet } = useContext(LocalizationContext);

  // const
  const sdkInit = sdkStore.initialized;
  const userFilledMessageListParams = queries ? queries.messageListParams: null;
  const disabled = !initialized
    || !isOnline
    || utils.isDisabledBecauseFrozen(currentOpenChannel, userId);
  // || utils.isDisabledBecauseMuted(mutedParticipantIds, userId)
  const userDefinedDisableUserProfile = disableUserProfile || config.disableUserProfile;
  const userDefinedRenderProfile = renderUserProfile || config.renderUserProfile;

  // useMemo
  const amIBanned = useMemo(() => {
    return bannedParticipantIds.indexOf(user.userId) >= 0;
  }, [channelUrl, bannedParticipantIds, user]);
  const amIMuted = useMemo(() => {
    return mutedParticipantIds.indexOf(user.userId) >= 0;
  }, [channelUrl, mutedParticipantIds, user]);
  const amIOperator = useMemo(() => {
    return operators.map(operator => operator.userId).indexOf(user.userId) >= 0;
  }, [channelUrl, operators, user]);

  // use hooks
  useSetChannel(
    { channelUrl, sdkInit, fetchingParticipants },
    { sdk, logger, messagesDispatcher },
  );

  useEffect(() => {
    if (renderCustomMessage) {
      // eslint-disable-next-line no-console
      console.info('The parameter type of renderCustomMessage will be changed to the object in the next minor update.');
    }
  }, []);

  const checkScrollBottom = useCheckScrollBottom(
    { conversationScrollRef },
    { logger },
  );
  useHandleChannelEvents(
    { currentOpenChannel, checkScrollBottom },
    { sdk, logger, messagesDispatcher },
  );
  useInitialMessagesFetch(
    { currentOpenChannel, userFilledMessageListParams },
    { sdk, logger, messagesDispatcher },
  );

  // donot fetch more for streaming
  const fetchMore = utils.shouldFetchMore(allMessages?.length, experimentalMessageLimit);
  const onScroll = useScrollCallback(
    { currentOpenChannel, lastMessageTimestamp, fetchMore },
    { sdk, logger, messagesDispatcher, hasMore, userFilledMessageListParams },
  );
  const handleSendMessage = useSendMessageCallback(
    { currentOpenChannel, onBeforeSendUserMessage, checkScrollBottom, messageInputRef },
    { sdk, logger, messagesDispatcher },
  );
  const handleFileUpload = useFileUploadCallback(
    { currentOpenChannel, onBeforeSendFileMessage, checkScrollBottom, imageCompression },
    { sdk, logger, messagesDispatcher },
  );
  const updateMessage = useUpdateMessageCallback(
    { currentOpenChannel, onBeforeSendUserMessage },
    { sdk, logger, messagesDispatcher },
  );
  const deleteMessage = useDeleteMessageCallback(
    { currentOpenChannel },
    { logger, messagesDispatcher },
  );
  const resendMessage = useResendMessageCallback(
    { currentOpenChannel },
    { logger, messagesDispatcher },
  );

  useTrimMessageList(
    { messagesLength: allMessages?.length, experimentalMessageLimit },
    { messagesDispatcher, logger }
  );

  // handle API calls from withSendbird
  useEffect(() => {
    const subscriber = new Map();
    if (!pubSub || !pubSub.subscribe) {
      return;
    }
    subscriber.set(topics.SEND_USER_MESSAGE, pubSub.subscribe(topics.SEND_USER_MESSAGE, (msg) => {
      const { channel, message } = msg;
      scrollIntoLast();
      if (channel && (channelUrl === channel.url)) {
        messagesDispatcher({
          type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
          payload: message,
        });
      }
    }));
    subscriber.set(topics.SEND_MESSAGE_START, pubSub.subscribe(topics.SEND_MESSAGE_START, (msg) => {
      const { channel, message } = msg;
      if (channel && (channelUrl === channel.url)) {
        messagesDispatcher({
          type: messageActionTypes.SENDING_MESSAGE_START,
          payload: { message, channel },
        });
      }
    }));
    subscriber.set(topics.SEND_FILE_MESSAGE, pubSub.subscribe(topics.SEND_FILE_MESSAGE, (msg) => {
      const { channel, message } = msg;
      scrollIntoLast();
      if (channel && (channelUrl === channel.url)) {
        messagesDispatcher({
          type: messageActionTypes.SENDING_MESSAGE_SUCCEEDED,
          payload: { message, channel },
        });
      }
    }));
    subscriber.set(topics.UPDATE_USER_MESSAGE, pubSub.subscribe(topics.UPDATE_USER_MESSAGE, (msg) => {
      const { channel, message, fromSelector } = msg;
      if (fromSelector && channel && (channelUrl === channel.url)) {
        messagesDispatcher({
          type: messageActionTypes.ON_MESSAGE_UPDATED,
          payload: { channel, message },
        });
      }
    }));
    subscriber.set(topics.DELETE_MESSAGE, pubSub.subscribe(topics.DELETE_MESSAGE, (msg) => {
      const { channel, messageId } = msg;
      if (channel && (channelUrl === channel.url)) {
        messagesDispatcher({
          type: messageActionTypes.ON_MESSAGE_DELETED,
          payload: messageId,
        });
      }
    }));

    return () => {
      if (subscriber) {
        subscriber.forEach((s) => {
          try {
            s.remove();
          } catch {
            //
          }
        });
      }
    };
  }, [channelUrl, sdkInit]);

  if (
    !currentOpenChannel
    || !currentOpenChannel.url
    || amIBanned
  ) {
    return (<div className={COMPONENT_CLASS_NAME}><PlaceHolder type={PlaceHolderTypes.NO_CHANNELS} /></div>);
  }
  if (loading) {
    return (<div className={COMPONENT_CLASS_NAME}><PlaceHolder type={PlaceHolderTypes.LOADING} /></div>);
  }
  if (isInvalid) {
    return (<div className={COMPONENT_CLASS_NAME}><PlaceHolder type={PlaceHolderTypes.WRONG} /></div>);
  }

  return (
    <UserProfileProvider
      className={COMPONENT_CLASS_NAME}
      disableUserProfile={userDefinedDisableUserProfile}
      renderUserProfile={userDefinedRenderProfile}
    >
      {
        renderChannelTitle
          ? renderChannelTitle({
            channel: currentOpenChannel,
            user: user,
          })
          : (
            <OpenchannelConversationHeader
              title={currentOpenChannel.name}
              subTitle={`${utils.kFormatter(currentOpenChannel.participantCount)} ${stringSet.OPEN_CHANNEL_CONVERSATION__TITLE_PARTICIPANTS}`}
              coverImage={currentOpenChannel.coverUrl}
              onActionClick={onChatHeaderActionClick}
              amIOperator={amIOperator}
            />
          )
      }
      {
        currentOpenChannel.isFrozen && (
          <FrozenChannelNotification />
        )
      }
      <OpenchannelConversationScroll
        ref={conversationScrollRef}
        renderCustomMessage={renderCustomMessage}
        openchannel={currentOpenChannel}
        user={user}
        useMessageGrouping={useMessageGrouping}
        isOnline={isOnline}
        allMessages={allMessages}
        onScroll={onScroll}
        hasMore={hasMore}
        updateMessage={updateMessage}
        deleteMessage={deleteMessage}
        resendMessage={resendMessage}
      />
      {
        renderMessageInput
          ? (
            <div className="sendbird-openchannel-footer">
              {
                renderMessageInput({
                  channel: currentOpenChannel,
                  user: user,
                  disabled: disabled,
                })
              }
            </div>
          )
          : (
            <MessageInputWrapper
              channel={currentOpenChannel}
              user={user}
              ref={messageInputRef}
              disabled={disabled || amIMuted}
              onSendMessage={handleSendMessage}
              onFileUpload={handleFileUpload}
              renderMessageInput={renderMessageInput}
            />
          )
      }
    </UserProfileProvider>
  );
};

export default withSendbirdContext(OpenchannelConversation);
