            disabled
            name="sendbird-edit-user-profile__userid__input"
            value={user.userId}
          />
        </section>
        <section className="sendbird-edit-user-profile__theme">
          <InputLabel>
            {stringSet.EDIT_PROFILE__THEME_LABEL}
