export const RESET_MESSAGES = 'RESET_MESSAGES';

export const FETCH_INITIAL_MESSAGES_START = 'FETCH_INITIAL_MESSAGES_START';
export const FETCH_INITIAL_MESSAGES_SUCCESS = 'FETCH_INITIAL_MESSAGES_SUCCESS';
export const FETCH_INITIAL_MESSAGES_FAILURE = 'FETCH_INITIAL_MESSAGES_FAILURE';
export const FETCH_PREV_MESSAGES_SUCCESS = 'FETCH_PREV_MESSAGES_SUCCESS';
export const FETCH_PREV_MESSAGES_FAILURE = 'FETCH_PREV_MESSAGES_FAILURE';
export const FETCH_NEXT_MESSAGES_SUCCESS = 'FETCH_NEXT_MESSAGES_SUCCESS';
export const FETCH_NEXT_MESSAGES_FAILURE = 'FETCH_NEXT_MESSAGES_FAILURE';

export const SEND_MESSAGEGE_START = 'SEND_MESSAGEGE_START';
export const SEND_MESSAGEGE_SUCESS = 'SEND_MESSAGEGE_SUCESS';
export const SEND_MESSAGEGE_FAILURE = 'SEND_MESSAGEGE_FAILURE';

export const RESEND_MESSAGEGE_START = 'RESEND_MESSAGEGE_START';

export const ON_MESSAGE_RECEIVED = 'ON_MESSAGE_RECEIVED';
export const ON_MESSAGE_UPDATED = 'ON_MESSAGE_UPDATED';
export const ON_MESSAGE_THREAD_INFO_UPDATED = 'ON_MESSAGE_THREAD_INFO_UPDATED';
export const ON_MESSAGE_DELETED = 'ON_MESSAGE_DELETED';
export const ON_MESSAGE_DELETED_BY_REQ_ID = 'ON_MESSAGE_DELETED_BY_REQ_ID';

export const SET_CURRENT_CHANNEL = 'SET_CURRENT_CHANNEL';
export const SET_CHANNEL_INVALID = 'SET_CHANNEL_INVALID';
export const MARK_AS_READ = 'MARK_AS_READ';

export const ON_REACTION_UPDATED = 'ON_REACTION_UPDATED';
export const SET_EMOJI_CONTAINER = 'SET_EMOJI_CONTAINER';

export const MESSAGE_LIST_PARAMS_CHANGED = 'MESSAGE_LIST_PARAMS_CHANGED';
import format from 'date-fns/format';
import * as channelActions from './dux/actionTypes';
import topics from '../../../lib/pubSub/topics';

import { getSendingMessageStatus, isReadMessage } from '../../../utils';
import { OutgoingMessageStates } from '../../../utils/exports/getOutgoingMessageState';

const UNDEFINED = 'undefined';
const { SUCCEEDED, FAILED, PENDING } = getSendingMessageStatus();

export const scrollToRenderedMessage = (scrollRef, initialTimeStamp) => {
  try {
    const container = scrollRef.current;
    // scroll into the message with initialTimeStamp
    const element = container.querySelectorAll(`[data-sb-created-at="${initialTimeStamp}"]`)?.[0];
    if (element) {
      // Calculate the offset of the element from the top of the container
      const containerHeight = container.offsetHeight;
      const elementHeight = element.offsetHeight;
      const elementOffset = (containerHeight - elementHeight) / 2;
      // Set the scroll position of the container to bring the element to the middle
      container.scrollTop = element.offsetTop - elementOffset;
    }
  } catch {
    // do nothing
  }
};

/* eslint-disable default-param-last */
export const scrollIntoLast = (initialTry = 0, scrollRef) => {
  const MAX_TRIES = 10;
  const currentTry = initialTry;
  if (currentTry > MAX_TRIES) {
    return;
  }
  try {
    const scrollDOM = scrollRef?.current || document.querySelector('.sendbird-conversation__messages-padding');
    // eslint-disable-next-line no-multi-assign
    scrollDOM.scrollTop = scrollDOM.scrollHeight;
  } catch (error) {
    setTimeout(() => {
      scrollIntoLast(currentTry + 1, scrollRef);
    }, 500 * currentTry);
  }
};

export const pubSubHandleRemover = (subscriber) => {
  subscriber.forEach((s) => {
    try {
      s.remove();
    } catch {
      //
    }
  });
};

export const pubSubHandler = ({
  channelUrl,
  pubSub,
  dispatcher,
  scrollRef,
}) => {
  const subscriber = new Map();
  if (!pubSub || !pubSub.subscribe) return subscriber;
  subscriber.set(topics.SEND_USER_MESSAGE, pubSub.subscribe(topics.SEND_USER_MESSAGE, (msg) => {
    const { channel, message } = msg;
    scrollIntoLast(0, scrollRef);
    if (channelUrl === channel?.url) {
      dispatcher({
        type: channelActions.SEND_MESSAGEGE_SUCESS,
        payload: message,
      });
    }
  }));
  subscriber.set(topics.SEND_MESSAGE_START, pubSub.subscribe(topics.SEND_MESSAGE_START, (msg) => {
    const { channel, message } = msg;
    if (channelUrl === channel?.url) {
      dispatcher({
        type: channelActions.SEND_MESSAGEGE_START,
        payload: message,
      });
    }
  }));
  subscriber.set(topics.SEND_FILE_MESSAGE, pubSub.subscribe(topics.SEND_FILE_MESSAGE, (msg) => {
    const { channel, message } = msg;
    scrollIntoLast(0, scrollRef);
    if (channelUrl === channel?.url) {
      dispatcher({
        type: channelActions.SEND_MESSAGEGE_SUCESS,
        payload: message,
      });
    }
  }));
  subscriber.set(topics.UPDATE_USER_MESSAGE, pubSub.subscribe(topics.UPDATE_USER_MESSAGE, (msg) => {
    const { channel, message, fromSelector } = msg;
    if (fromSelector && (channelUrl === channel?.url)) {
      dispatcher({
        type: channelActions.ON_MESSAGE_UPDATED,
        payload: { channel, message },
      });
    }
  }));
  subscriber.set(topics.DELETE_MESSAGE, pubSub.subscribe(topics.DELETE_MESSAGE, (msg) => {
    const { channel, messageId } = msg;
    if (channelUrl === channel?.url) {
      dispatcher({
        type: channelActions.ON_MESSAGE_DELETED,
        payload: messageId,
      });
    }
  }));

  return subscriber;
};

export const getParsedStatus = (message, currentGroupChannel) => {
  if (message.requestState === FAILED) {
    return OutgoingMessageStates.FAILED;
  }

  if (message.requestState === PENDING) {
    return OutgoingMessageStates.PENDING;
  }

  if (message.requestState === SUCCEEDED) {
    if (!currentGroupChannel) {
      return OutgoingMessageStates.SENT;
    }

    const unreadMemberCount = currentGroupChannel?.getUnreadMemberCount(message);
    if (unreadMemberCount === 0) {
      return OutgoingMessageStates.READ;
    }

    const isDelivered = currentGroupChannel?.getUndeliveredMemberCount(message) === 0;
    if (isDelivered) {
      return OutgoingMessageStates.DELIVERED;
    }

    return OutgoingMessageStates.SENT;
  }

  return null;
};

export const isOperator = (groupChannel = {}) => {
  const myRole = groupChannel?.myRole;
  return myRole === 'operator';
};

export const isDisabledBecauseFrozen = (groupChannel = {}) => {
  const isFrozen = groupChannel?.isFrozen;
  return isFrozen && !isOperator(groupChannel);
};

export const isDisabledBecauseMuted = (groupChannel = {}) => {
  const myMutedState = groupChannel?.myMutedState;
  return myMutedState === 'muted';
};

export const getEmojiCategoriesFromEmojiContainer = (emojiContainer = {}) => (
  emojiContainer.emojiCategories ? emojiContainer.emojiCategories : []
);

export const getAllEmojisFromEmojiContainer = (emojiContainer = {}) => {
  const { emojiCategories = [] } = emojiContainer;
  const allEmojis = [];

  for (let categoryIndex = 0; categoryIndex < emojiCategories.length; categoryIndex += 1) {
    const { emojis } = emojiCategories[categoryIndex];
    for (let emojiIndex = 0; emojiIndex < emojis.length; emojiIndex += 1) {
      allEmojis.push(emojis[emojiIndex]);
    }
  }
  return allEmojis;
};

export const getEmojisFromEmojiContainer = (emojiContainer = {}, emojiCategoryId = '') => (
  emojiContainer.emojiCategories
    ? emojiContainer.emojiCategories
      .filter((emojiCategory) => emojiCategory.id === emojiCategoryId)[0].emojis
    : []
);

export const getAllEmojisMapFromEmojiContainer = (emojiContainer = {}) => {
  const { emojiCategories = [] } = emojiContainer;
  const allEmojisMap = new Map();

  for (let categoryIndex = 0; categoryIndex < emojiCategories.length; categoryIndex += 1) {
    const { emojis } = emojiCategories[categoryIndex];
    for (let emojiIndex = 0; emojiIndex < emojis.length; emojiIndex += 1) {
      const { key, url } = emojis[emojiIndex];
      allEmojisMap.set(key, url);
    }
  }
  return allEmojisMap;
};

export const getNicknamesMapFromMembers = (members = []) => {
  const nicknamesMap = new Map();
  for (let memberIndex = 0; memberIndex < members.length; memberIndex += 1) {
    const { userId, nickname } = members[memberIndex];
    nicknamesMap.set(userId, nickname);
  }
  return nicknamesMap;
};

const getUniqueListBy = (arr, key) => [...new Map(arr.map((item) => [item[key], item])).values()];
const getUniqueListByMessageId = (arr) => getUniqueListBy(arr, 'messageId');
const sortByCreatedAt = (messages) => messages.sort((a, b) => a.createdAt - b.createdAt);

export const mergeAndSortMessages = (oldMessages, newMessages) => {
  const lastOldMessage = oldMessages[oldMessages.length - 1];
  const firstNewMessage = newMessages[0];
  // If the last message of oldMessages is older than the first message of newMessages,
  // then we can safely append newMessages to oldMessages.
  if (lastOldMessage?.createdAt < firstNewMessage?.createdAt) {
    return [...oldMessages, ...newMessages];
  }

  // todo: optimize this
  // If the last message of oldMessages is newer than the first message of newMessages,
  // then we need to merge the two arrays and sort them by createdAt.
  const mergedMessages = [...oldMessages, ...newMessages];
  const unique = getUniqueListByMessageId(mergedMessages);
  return sortByCreatedAt(unique);
};

export const getMessageCreatedAt = (message) => format(message.createdAt, 'p');

export const isSameGroup = (message, comparingMessage, currentChannel) => {
  if (!(message
    && comparingMessage
    && message.messageType
    && message.messageType !== 'admin'
    && comparingMessage.messageType
    && comparingMessage?.messageType !== 'admin'
    && message?.sender
    && comparingMessage?.sender
    && message?.createdAt
    && comparingMessage?.createdAt
    && message?.sender?.userId
    && comparingMessage?.sender?.userId
  )) {
    return false;
  }
  return (
    message?.sendingStatus === comparingMessage?.sendingStatus
    && message?.sender?.userId === comparingMessage?.sender?.userId
    && getMessageCreatedAt(message) === getMessageCreatedAt(comparingMessage)
    && isReadMessage(currentChannel, message) === isReadMessage(currentChannel, comparingMessage)
  );
};

export const compareMessagesForGrouping = (
  prevMessage,
  currMessage,
  nextMessage,
  currentChannel,
  replyType,
) => {
  if (replyType === 'THREAD' && currMessage?.threadInfo) {
    return [false, false];
  }
  const sendingStatus = currMessage?.sendingStatus || '';
  const isAcceptable = sendingStatus !== 'pending' && sendingStatus !== 'failed';
  return [
    isSameGroup(prevMessage, currMessage, currentChannel) && isAcceptable,
    isSameGroup(currMessage, nextMessage, currentChannel) && isAcceptable,
  ];
};

export const hasOwnProperty = (property) => (payload) => {
  // eslint-disable-next-line no-prototype-builtins
  if (payload && payload.hasOwnProperty && payload.hasOwnProperty(property)) {
    return true;
  }
  return false;
};

export const passUnsuccessfullMessages = (allMessages, newMessage) => {
  const { sendingStatus = UNDEFINED } = newMessage;
  if (sendingStatus === SUCCEEDED || sendingStatus === PENDING) {
    const lastIndexOfSucceededMessage = allMessages
      .map((message) => (
        message.sendingStatus
        || ((message.isAdminMessage && message.isAdminMessage()) ? SUCCEEDED : UNDEFINED)
      ))
      .lastIndexOf(SUCCEEDED);
    if (lastIndexOfSucceededMessage + 1 < allMessages.length) {
      const messages = [...allMessages];
      messages.splice(lastIndexOfSucceededMessage + 1, 0, newMessage);
      return messages;
    }
  }
  return [
    ...allMessages,
    newMessage,
  ];
};

export const pxToNumber = (px) => {
  if (typeof px === 'number') {
    return px;
  }
  if (typeof px === 'string') {
    const parsed = Number.parseFloat(px);
    if (!Number.isNaN(parsed)) {
      return parsed;
    }
  }
  return null;
};

export const isAboutSame = (a, b, px) => (Math.abs(a - b) <= px);

export default getParsedStatus;
/**
 * This function helps consider the every condition
 * related to enabling emoji reaction feature.
 */

export interface IsReactionEnabledProps {
  isBroadcast?: boolean;
  isSuper?: boolean;
  globalLevel?: boolean;
  moduleLevel?: boolean;
}

export function getIsReactionEnabled({
  isBroadcast = false,
  isSuper = false,
  globalLevel = true,
  moduleLevel,
}: IsReactionEnabledProps): boolean {
  return !(isBroadcast || isSuper) && (moduleLevel ?? globalLevel);
}
export default {
  initialized: false,
  loading: true,
  allMessages: [],
  currentGroupChannel: { members: [] },
  // for scrollup
  hasMorePrev: false,
  oldestMessageTimeStamp: 0,
  // for scroll down
  // onScrollDownCallback is added for navigation to different timestamps on messageSearch
  // hasMorePrev, onScrollCallback -> scroll up(default behavior)
  // hasMoreNext, onScrollDownCallback -> scroll down
  hasMoreNext: false,
  latestMessageTimeStamp: 0,
  emojiContainer: {},
  unreadSince: null,
  isInvalid: false,
  messageListParams: null,
};
import format from 'date-fns/format';

import * as actionTypes from './actionTypes';
import compareIds from '../../../../utils/compareIds';
import { PREV_RESULT_SIZE, NEXT_RESULT_SIZE } from '../const';
import { passUnsuccessfullMessages, mergeAndSortMessages } from '../utils';
import { filterMessageListParams, getSendingMessageStatus } from '../../../../utils';

const {
  SUCCEEDED,
} = getSendingMessageStatus();
const getOldestMessageTimeStamp = (messages = []) => {
  const oldestMessage = messages[0];
  return (oldestMessage && oldestMessage.createdAt) || null;
};
const getLatestMessageTimeStamp = (messages = []) => {
  const latestMessage = messages[messages.length - 1];
  return (latestMessage && latestMessage.createdAt) || null;
};

export default function reducer(state, action) {
  switch (action.type) {
    case actionTypes.RESET_MESSAGES:
      return {
        ...state,
        // when user switches channel, if the previous channel `hasMorePrev`
        // the onScroll gets called twice, setting hasMorePrev false prevents this
        hasMorePrev: false,
        hasMoreNext: false,
        allMessages: [],
      };
    case actionTypes.FETCH_INITIAL_MESSAGES_START: {
      return {
        ...state,
        loading: true,
        allMessages: [
          ...state.allMessages.filter((m) => (
            m.sendingStatus !== SUCCEEDED
          )),
        ],
      };
    }
    case actionTypes.FETCH_INITIAL_MESSAGES_SUCCESS: {
      const {
        currentGroupChannel,
        messages,
      } = action.payload;
      if (!(currentGroupChannel?.url === state.currentGroupChannel?.url)) {
        return state;
      }
      const oldestMessageTimeStamp = getOldestMessageTimeStamp(messages);
      const latestMessageTimeStamp = getLatestMessageTimeStamp(messages);
      return {
        ...state,
        loading: false,
        initialized: true,
        hasMorePrev: true,
        hasMoreNext: true,
        oldestMessageTimeStamp,
        latestMessageTimeStamp,
        allMessages: [...messages],
      };
    }
    case actionTypes.FETCH_PREV_MESSAGES_SUCCESS: {
      const {
        currentGroupChannel,
        messages,
      } = action.payload;
      if (!(currentGroupChannel?.url === state.currentGroupChannel?.url)) {
        return state;
      }
      const hasMorePrev = messages && messages.length === PREV_RESULT_SIZE + 1;
      const oldestMessageTimeStamp = getOldestMessageTimeStamp(messages);

      // Remove duplicated messages
      const duplicatedMessageIds = [];
      const updatedOldMessages = state.allMessages.map((msg) => {
        const duplicatedMessage = messages.find(({ messageId }) => (
          compareIds(messageId, msg.messageId)
        ));
        if (!duplicatedMessage) {
          return msg;
        }
        duplicatedMessageIds.push(duplicatedMessage.messageId);
        return (duplicatedMessage.updatedAt > msg.updatedAt) ? duplicatedMessage : msg;
      });
      const filteredNewMessages = (duplicatedMessageIds.length > 0)
        ? messages.filter((msg) => (
          !duplicatedMessageIds.find((messageId) => compareIds(messageId, msg.messageId))
        ))
        : messages;

      return {
        ...state,
        hasMorePrev,
        oldestMessageTimeStamp,
        allMessages: [
          ...filteredNewMessages,
          ...updatedOldMessages,
        ],
      };
    }
    case actionTypes.FETCH_NEXT_MESSAGES_SUCCESS: {
      const {
        currentGroupChannel,
        messages,
      } = action.payload;
      if (!(currentGroupChannel?.url === state.currentGroupChannel?.url)) {
        return state;
      }
      const hasMoreNext = messages && messages.length === NEXT_RESULT_SIZE + 1;
      const latestMessageTimeStamp = getLatestMessageTimeStamp(messages);

      // sort ~
      const sortedMessages = mergeAndSortMessages(state.allMessages, messages);

      return {
        ...state,
        hasMoreNext,
        latestMessageTimeStamp,
        allMessages: sortedMessages,
      };
    }
    case actionTypes.FETCH_INITIAL_MESSAGES_FAILURE:
    case actionTypes.FETCH_PREV_MESSAGES_FAILURE:
    case actionTypes.FETCH_NEXT_MESSAGES_FAILURE: {
      const { currentGroupChannel } = action.payload;
      if (currentGroupChannel?.url !== state?.currentGroupChannel?.url) {
        return state;
      }
      return {
        ...state,
        loading: false,
        initialized: false,
        allMessages: [],
        hasMorePrev: false,
        hasMoreNext: false,
        oldestMessageTimeStamp: null,
        latestMessageTimeStamp: null,
      };
    }
    case actionTypes.SEND_MESSAGEGE_START:
      return {
        ...state,
        allMessages: [
          ...state.allMessages,
          { ...action.payload },
        ],
      };
    case actionTypes.SEND_MESSAGEGE_SUCESS: {
      const message = action.payload;
      const filteredMessages = state.allMessages.filter((m) => (
        m?.reqId !== message?.reqId
      ));
      const pendingIndex = filteredMessages.findIndex((msg) => (
        msg?.sendingStatus === 'pending' || msg?.sendingStatus === 'failed'
      ));
      return {
        ...state,
        allMessages: pendingIndex > -1
          ? [
            ...filteredMessages.slice(0, pendingIndex),
            message,
            ...filteredMessages.slice(pendingIndex),
          ]
          : [
            ...filteredMessages,
            message,
          ],
      };
    }
    case actionTypes.SEND_MESSAGEGE_FAILURE: {
      // eslint-disable-next-line no-param-reassign
      action.payload.failed = true;
      return {
        ...state,
        allMessages: state.allMessages.map((m) => (
          compareIds(m.reqId, action.payload.reqId)
            ? action.payload
            : m
        )),
      };
    }
    case actionTypes.SET_CURRENT_CHANNEL: {
      return {
        ...state,
        currentGroupChannel: action.payload,
        isInvalid: false,
      };
    }
    case actionTypes.SET_CHANNEL_INVALID: {
      return {
        ...state,
        currentGroupChannel: null,
        isInvalid: true,
      };
    }
    case actionTypes.ON_MESSAGE_RECEIVED: {
      const { channel, message } = action.payload;
      const { members } = channel;
      const { sender } = message;
      const { currentGroupChannel = {}, unreadSince } = state;
      const currentGroupChannelUrl = currentGroupChannel?.url;

      if (!compareIds(channel?.url, currentGroupChannelUrl)) {
        return state;
      }
      // Excluded overlapping messages
      if (state.allMessages.some((msg) => msg.messageId === message.messageId)) {
        return state;
      }
      // Filter by userFilledQuery
      if (state.messageListParams && !filterMessageListParams(state.messageListParams, message)) {
        return state;
      }

      if (message.isAdminMessage && message.isAdminMessage()) {
        return {
          ...state,
          allMessages: passUnsuccessfullMessages(state.allMessages, message),
        };
      }

      // Update members when sender profileUrl, nickname, friendName has been changed
      const senderMember = members?.find((m) => (m?.userId === sender?.userId));
      if ((senderMember?.profileUrl !== sender?.profileUrl)
        || (senderMember?.friendName !== sender?.friendName)
        || (senderMember?.nickname !== sender?.nickname)) {
        channel.members = members.map((member) => {
          if (member.userId === sender.userId) {
            return sender;
          }
          return member;
        });
      }
      return {
        ...state,
        currentGroupChannel: channel,
        unreadSince: state?.unreadSince ? unreadSince : format(new Date(), 'p MMM dd'),
        allMessages: passUnsuccessfullMessages(state.allMessages, message),
      };
    }
    case actionTypes.ON_MESSAGE_UPDATED: {
      const { channel, message } = action.payload;
      const currentGroupChannelUrl = state?.currentGroupChannel?.url || '';
      if (!compareIds(channel?.url, currentGroupChannelUrl)) {
        return state; // Ignore event when it is not for the current channel
      }
      if (state.messageListParams && !filterMessageListParams(state.messageListParams, message)) {
        // Delete the message if it doesn't match to the params anymore
        return {
          ...state,
          allMessages: state.allMessages.filter((m) => (
            !compareIds(m.messageId, message?.messageId)
          )),
        };
      }
      return {
        ...state,
        allMessages: state.allMessages.map((m) => {
          if (compareIds(m.messageId, message.messageId)) {
            return message;
          }
          if (compareIds(m.parentMessageId, message.messageId)) {
            m.parentMessage = message;// eslint-disable-line no-param-reassign
          }
          return m;
        }),
      };
    }
    case actionTypes.ON_MESSAGE_THREAD_INFO_UPDATED: {
      const { channel, event } = action.payload;
      const { channelUrl, threadInfo, targetMessageId } = event;
      const currentGroupChannelUrl = state?.currentGroupChannel?.url || '';
      if (
        !compareIds(channel?.url, currentGroupChannelUrl)
        || !compareIds(channel?.url, channelUrl)
      ) {
        return state; // Ignore event when it is not for the current channel
      }
      return {
        ...state,
        allMessages: state.allMessages.map((m) => {
          if (compareIds(m.messageId, targetMessageId)) {
            // eslint-disable-next-line no-param-reassign
            m.threadInfo = threadInfo; // Upsert threadInfo to the target message
          }
          return m;
        }),
      };
    }
    case actionTypes.RESEND_MESSAGEGE_START:
      return {
        ...state,
        allMessages: state.allMessages.map((m) => (
          compareIds(m.reqId, action.payload.reqId)
            ? action.payload
            : m
        )),
      };
    case actionTypes.MARK_AS_READ:
      if (state.currentGroupChannel?.url !== action.payload?.channel?.url) {
        return state;
      }
      return {
        ...state,
        unreadSince: null,
      };
    case actionTypes.ON_MESSAGE_DELETED:
      return {
        ...state,
        allMessages: state.allMessages.filter((m) => (
          !compareIds(m.messageId, action.payload)
        )),
      };
    case actionTypes.ON_MESSAGE_DELETED_BY_REQ_ID:
      return {
        ...state,
        allMessages: state.allMessages.filter((m) => (
          !compareIds(m.reqId, action.payload)
        )),
      };
    case actionTypes.SET_EMOJI_CONTAINER: {
      return {
        ...state,
        emojiContainer: action.payload,
      };
    }
    case actionTypes.ON_REACTION_UPDATED: {
      return {
        ...state,
        allMessages: state.allMessages.map((m) => {
          if (compareIds(m.messageId, action.payload.messageId)) {
            if (m.applyReactionEvent && typeof m.applyReactionEvent === 'function') {
              m.applyReactionEvent(action.payload);
            }
            return m;
          }
          return m;
        }),
      };
    }
    case actionTypes.MESSAGE_LIST_PARAMS_CHANGED: {
      return {
        ...state,
        messageListParams: action.payload,
      };
    }
    default:
      return state;
  }
}
import { useEffect } from 'react';
import { GroupChannel, GroupChannelHandler, SendbirdGroupChat } from '@sendbird/chat/groupChannel';
import { FileMessage, UserMessage } from '@sendbird/chat/message';

import { scrollIntoLast } from '../utils';

import { CustomUseReducerDispatcher, Logger } from '../../../../lib/SendbirdState';
import uuidv4 from '../../../../utils/uuid';
import compareIds from '../../../../utils/compareIds';
import * as messageActions from '../dux/actionTypes';
import useSendbirdStateContext from '../../../../hooks/useSendbirdStateContext';

/**
 * Handles ChannelEvents and send values to dispatcher using messagesDispatcher
 * messagesDispatcher: Dispatcher
 * sdk: sdkInstance
 * logger: loggerInstance
 * channelUrl: string
 * sdkInit: bool
 */
interface DynamicParams {
  sdkInit: boolean;
  currentUserId: string;
  currentGroupChannel: GroupChannel;
  disableMarkAsRead: boolean;
}
interface StaticParams {
  sdk: SendbirdGroupChat;
  logger: Logger;
  scrollRef: React.RefObject<HTMLDivElement>;
  setQuoteMessage: React.Dispatch<React.SetStateAction<UserMessage | FileMessage>>;
  messagesDispatcher: CustomUseReducerDispatcher;
}

const DELIVERY_RECIPT = 'delivery_receipt';

function useHandleChannelEvents({
  sdkInit,
  currentUserId,
  currentGroupChannel,
  disableMarkAsRead,
}: DynamicParams, {
  sdk,
  logger,
  scrollRef,
  setQuoteMessage,
  messagesDispatcher,
}: StaticParams): void {
  const store = useSendbirdStateContext();
  const {
    markAsReadScheduler,
    markAsDeliveredScheduler,
    disableMarkAsDelivered,
  } = store.config;
  const canSetMarkAsDelivered = store.stores.sdkStore.sdk?.appInfo?.premiumFeatureList
    ?.find((feature) => (feature === DELIVERY_RECIPT));

  useEffect(() => {
    const channelUrl = currentGroupChannel?.url;
    const channelHandlerId = uuidv4();
    if (channelUrl && sdkInit) {
      const channelHandler: GroupChannelHandler = {
        onMessageReceived: (channel, message) => {
          if (compareIds(channel?.url, channelUrl)) {
            let scrollToEnd = false;
            try {
              const { current } = scrollRef;
              scrollToEnd = current.offsetHeight + current.scrollTop >= current.scrollHeight - 10;
              // 10 is a buffer
            } catch (error) {
              //
            }

            logger.info('Channel | useHandleChannelEvents: onMessageReceived', message);
            messagesDispatcher({
              type: messageActions.ON_MESSAGE_RECEIVED,
              payload: { channel, message },
            });
            if (scrollToEnd
              && document.getElementById('sendbird-dropdown-portal')?.childElementCount === 0
              && document.getElementById('sendbird-emoji-list-portal')?.childElementCount === 0
            ) {
              // and !openContextMenu
              try {
                setTimeout(() => {
                  scrollIntoLast(0, scrollRef);
                });
                if (!disableMarkAsRead) {
                  markAsReadScheduler.push(currentGroupChannel);
                }
                if (canSetMarkAsDelivered && !disableMarkAsDelivered) {
                  markAsDeliveredScheduler.push(currentGroupChannel);
                }
              } catch (error) {
                logger.warning('Channel | onMessageReceived | scroll to end failed');
              }
            }
          }
        },
        onUnreadMemberStatusUpdated: (channel) => {
          logger.info('Channel | useHandleChannelEvents: onUnreadMemberStatusUpdated', channel);
          if (compareIds(channel?.url, channelUrl)) {
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        // before(onDeliveryReceiptUpdated)
        onUndeliveredMemberStatusUpdated: (channel) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onDeliveryReceiptUpdated', channel);
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onMessageUpdated: (channel, message) => {
          logger.info('Channel | useHandleChannelEvents: onMessageUpdated', message);
          messagesDispatcher({
            type: messageActions.ON_MESSAGE_UPDATED,
            payload: { channel, message },
          });
        },
        onThreadInfoUpdated: (channel, threadInfoUpdateEvent) => {
          logger.info('Channel | useHandleChannelEvents: onThreadInfoUpdated', { channel, threadInfoUpdateEvent });
          messagesDispatcher({
            type: messageActions.ON_MESSAGE_THREAD_INFO_UPDATED,
            payload: { channel, event: threadInfoUpdateEvent },
          });
        },
        onMessageDeleted: (channel, messageId) => {
          logger.info('Channel | useHandleChannelEvents: onMessageDeleted', { channel, messageId });
          setQuoteMessage(null);
          messagesDispatcher({
            type: messageActions.ON_MESSAGE_DELETED,
            payload: messageId,
          });
        },
        onReactionUpdated: (channel, reactionEvent) => {
          logger.info('Channel | useHandleChannelEvents: onReactionUpdated', { channel, reactionEvent });
          messagesDispatcher({
            type: messageActions.ON_REACTION_UPDATED,
            payload: reactionEvent,
          });
        },
        onChannelChanged: (channel) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onChannelChanged', channel);
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onChannelFrozen: (channel) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onChannelFrozen', channel);
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onChannelUnfrozen: (channel) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onChannelUnFrozen', channel);
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onUserMuted: (channel, user) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onUserMuted', { channel, user });
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onUserUnmuted: (channel, user) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onUserUnmuted', { channel, user });
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onUserBanned: (channel, user) => {
          if (compareIds(channel?.url, channelUrl) && user?.userId === sdk?.currentUser?.userId) {
            logger.info('Channel | useHandleChannelEvents: onUserBanned', { channel, user });
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: null,
            });
          }
        },
        onOperatorUpdated: (channel, users) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onOperatorUpdated', { channel, users });
            messagesDispatcher({
              type: messageActions.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          }
        },
        onUserLeft: (channel, user) => {
          if (compareIds(channel?.url, channelUrl)) {
            logger.info('Channel | useHandleChannelEvents: onUserLeft', { channel, user });
            if (user?.userId === currentUserId) {
              messagesDispatcher({
                type: messageActions.SET_CURRENT_CHANNEL,
                payload: null,
              });
            }
          }
        },
      };
      logger.info('Channel | useHandleChannelEvents: Setup event handler', { channelHandlerId, channelHandler });
      // Add this group channel handler to the Sendbird chat instance
      sdk.groupChannel?.addGroupChannelHandler(channelHandlerId, new GroupChannelHandler(channelHandler));
    }
    return () => {
      if (sdk?.groupChannel?.removeGroupChannelHandler) {
        logger.info('Channel | useHandleChannelEvents: Removing message reciver handler', channelHandlerId);
        sdk.groupChannel.removeGroupChannelHandler(channelHandlerId);
      } else if (sdk?.groupChannel) {
        logger.error('Channel | useHandleChannelEvents: Not found the removeGroupChannelHandler');
      }
    };
  }, [currentGroupChannel?.url, sdkInit]);
}

export default useHandleChannelEvents;
import { useEffect } from 'react';

import * as messageActionTypes from '../dux/actionTypes';

function useSetChannel({ channelUrl, sdkInit, disableMarkAsRead }, {
  messagesDispatcher,
  sdk,
  logger,
  markAsReadScheduler,
}) {
  useEffect(() => {
    if (channelUrl && sdkInit && sdk && sdk.groupChannel) {
      logger.info('Channel | useSetChannel fetching channel', channelUrl);
      sdk.groupChannel.getChannel(channelUrl)
        .then((groupChannel) => {
          logger.info('Channel | useSetChannel fetched channel', groupChannel);
          messagesDispatcher({
            type: messageActionTypes.SET_CURRENT_CHANNEL,
            payload: groupChannel,
          });

          logger.info('Channel: Mark as read', groupChannel);
          if (!disableMarkAsRead) {
            markAsReadScheduler.push(groupChannel);
          }
        })
        .catch((e) => {
          logger.warning('Channel | useSetChannel fetch channel failed', { channelUrl, e });
          messagesDispatcher({
            type: messageActionTypes.SET_CHANNEL_INVALID,
          });
        });
      sdk.getAllEmoji().then((emojiContainer_) => {
        logger.info('Channel: Getting emojis success', emojiContainer_);
        messagesDispatcher({
          type: messageActionTypes.SET_EMOJI_CONTAINER,
          payload: emojiContainer_,
        });
      }).catch((err) => {
        logger.error('Channel: Getting emojis failed', err);
      });
      sdk.getAllEmoji();
    }
  }, [channelUrl, sdkInit]);
}

export default useSetChannel;
import { useEffect } from 'react';
import { ReplyType } from '@sendbird/chat/message';

import * as utils from '../utils';
import * as messageActionTypes from '../dux/actionTypes';
import { PREV_RESULT_SIZE, NEXT_RESULT_SIZE } from '../const';

function useInitialMessagesFetch({
  currentGroupChannel,
  userFilledMessageListQuery,
  initialTimeStamp,
  replyType,
}, {
  logger,
  scrollRef,
  messagesDispatcher,
}) {
  const channelUrl = currentGroupChannel?.url;
  useEffect(() => {
    logger.info('Channel useInitialMessagesFetch: Setup started', currentGroupChannel);
    messagesDispatcher({
      type: messageActionTypes.RESET_MESSAGES,
      payload: null,
    });

    if (currentGroupChannel && currentGroupChannel?.getMessagesByTimestamp) {
      const messageListParams = {};
      messageListParams.prevResultSize = PREV_RESULT_SIZE;
      if (initialTimeStamp) {
        messageListParams.nextResultSize = NEXT_RESULT_SIZE;
      }
      messageListParams.isInclusive = true;
      messageListParams.includeReactions = true;
      messageListParams.includeMetaArray = true;
      if (replyType && (replyType === 'QUOTE_REPLY' || replyType === 'THREAD')) {
        messageListParams.includeThreadInfo = true;
        messageListParams.includeParentMessageInfo = true;
        messageListParams.replyType = ReplyType.ONLY_REPLY_TO_CHANNEL;
      }
      if (userFilledMessageListQuery) {
        Object.keys(userFilledMessageListQuery).forEach((key) => {
          messageListParams[key] = userFilledMessageListQuery[key];
        });
      }
      if ((replyType && (replyType === 'QUOTE_REPLY' || replyType === 'THREAD')) || userFilledMessageListQuery) {
        logger.info('Channel useInitialMessagesFetch: Setup messageListParams', messageListParams);
        messagesDispatcher({
          type: messageActionTypes.MESSAGE_LIST_PARAMS_CHANGED,
          payload: messageListParams,
        });
      }

      logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });
      messagesDispatcher({
        type: messageActionTypes.FETCH_INITIAL_MESSAGES_START,
        payload: null,
      });

      currentGroupChannel.getMessagesByTimestamp(
        initialTimeStamp || new Date().getTime(),
        messageListParams,
      )
        .then((messages) => {
          messagesDispatcher({
            type: messageActionTypes.FETCH_INITIAL_MESSAGES_SUCCESS,
            payload: {
              currentGroupChannel,
              messages,
            },
          });
        })
        .catch((error) => {
          logger.error('Channel: Fetching messages failed', error);
          messagesDispatcher({
            type: messageActionTypes.FETCH_INITIAL_MESSAGES_FAILURE,
            payload: { currentGroupChannel },
          });
        })
        .finally(() => {
          if (!initialTimeStamp) {
            setTimeout(() => utils.scrollIntoLast(0, scrollRef));
          } else {
            setTimeout(() => {
              utils.scrollToRenderedMessage(scrollRef, initialTimeStamp);
            }, 500);
          }
        });
    }
  }, [channelUrl, userFilledMessageListQuery, initialTimeStamp]);
  /**
   * Note - useEffect(() => {}, [currentGroupChannel])
   * was buggy, that is why we did
   * const channelUrl = currentGroupChannel && currentGroupChannel.url;
   * useEffect(() => {}, [channelUrl])
   * Again, this hook is supposed to execute when currentGroupChannel changes
   * The 'channelUrl' here is not the same memory reference from Conversation.props
   */
}

export default useInitialMessagesFetch;
import { useEffect } from 'react';

import type { GroupChannel, SendbirdGroupChat } from '@sendbird/chat/groupChannel';
import { MessageListParams, ReplyType } from '@sendbird/chat/message';
import * as utils from '../utils';
import { PREV_RESULT_SIZE, NEXT_RESULT_SIZE } from '../const';
import * as messageActionTypes from '../dux/actionTypes';
import { Logger } from '../../../../lib/SendbirdState';
import { MarkAsReadSchedulerType } from '../../../../lib/hooks/useMarkAsReadScheduler';

interface DynamicParams {
  isOnline: boolean;
  replyType?: string;
  disableMarkAsRead: boolean;
}

interface StaticParams {
  logger: Logger;
  sdk: SendbirdGroupChat;
  currentGroupChannel: GroupChannel;
  scrollRef: React.RefObject<HTMLDivElement>;
  markAsReadScheduler: MarkAsReadSchedulerType;
  messagesDispatcher: (props: { type: string, payload: any }) => void;
  userFilledMessageListQuery?: Record<string, any>;
}

function useHandleReconnect(
  { isOnline, replyType, disableMarkAsRead }: DynamicParams,
  {
    logger,
    sdk,
    scrollRef,
    currentGroupChannel,
    messagesDispatcher,
    markAsReadScheduler,
    userFilledMessageListQuery,
  }: StaticParams,
): void {
  useEffect(() => {
    const wasOffline = !isOnline;
    return () => {
      // state changed from offline to online
      if (wasOffline && currentGroupChannel?.url) {
        logger.info('Refreshing conversation state');
        const isReactionEnabled = sdk?.appInfo?.useReaction || false;

        const messageListParams: MessageListParams = {
          prevResultSize: PREV_RESULT_SIZE,
          isInclusive: true,
          includeReactions: isReactionEnabled,
          nextResultSize: NEXT_RESULT_SIZE,
        };
        if (replyType && replyType === 'QUOTE_REPLY') {
          messageListParams.includeThreadInfo = true;
          messageListParams.includeParentMessageInfo = true;
          messageListParams.replyType = ReplyType.ONLY_REPLY_TO_CHANNEL;
        }
        if (userFilledMessageListQuery) {
          Object.keys(userFilledMessageListQuery).forEach((key) => {
            messageListParams[key] = userFilledMessageListQuery[key];
          });
        }
        logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });
        messagesDispatcher({
          type: messageActionTypes.FETCH_INITIAL_MESSAGES_START,
          payload: null,
        });

        sdk?.groupChannel?.getChannel(currentGroupChannel?.url)
          .then((groupChannel) => {
            const lastMessageTime = new Date().getTime();

            groupChannel.getMessagesByTimestamp(
              lastMessageTime,
              messageListParams,
            )
              .then((messages) => {
                messagesDispatcher({
                  type: messageActionTypes.FETCH_INITIAL_MESSAGES_SUCCESS,
                  payload: {
                    currentGroupChannel,
                    messages,
                  },
                });
                setTimeout(() => utils.scrollIntoLast(0, scrollRef));
              })
              .catch((error) => {
                logger.error('Channel: Fetching messages failed', error);
                messagesDispatcher({
                  type: messageActionTypes.FETCH_INITIAL_MESSAGES_FAILURE,
                  payload: { currentGroupChannel },
                });
              });
            if (!disableMarkAsRead) {
              markAsReadScheduler.push(currentGroupChannel);
            }
          });
      }
    };
  }, [isOnline, replyType]);
}

export default useHandleReconnect;
import { useCallback } from 'react';
import { ReplyType } from '@sendbird/chat/message';

import * as messageActionTypes from '../dux/actionTypes';
import { PREV_RESULT_SIZE } from '../const';

function useScrollCallback({
  currentGroupChannel,
  oldestMessageTimeStamp,
  userFilledMessageListQuery,
  replyType,
}, {
  hasMorePrev,
  logger,
  messagesDispatcher,
  sdk,
}) {
  return useCallback((cb) => {
    if (!hasMorePrev) { return; }
    const { appInfo = {} } = sdk;
    const isReactionEnabled = appInfo.useReaction || false;

    const messageListParams = {
      prevResultSize: PREV_RESULT_SIZE,
      isInclusive: true,
      includeReactions: isReactionEnabled,
    };
    messageListParams.includeMetaArray = true;
    if (replyType === 'QUOTE_REPLY' || replyType === 'THREAD') {
      messageListParams.includeThreadInfo = true;
      messageListParams.includeParentMessageInfo = true;
      messageListParams.replyType = ReplyType.ONLY_REPLY_TO_CHANNEL;
    }
    if (userFilledMessageListQuery) {
      Object.keys(userFilledMessageListQuery).forEach((key) => {
        messageListParams[key] = userFilledMessageListQuery[key];
      });
    }
    logger.info('Channel: Fetching messages', { currentGroupChannel, userFilledMessageListQuery });

    currentGroupChannel.getMessagesByTimestamp(
      oldestMessageTimeStamp || new Date().getTime(),
      messageListParams,
    )
      .then((messages) => {
        messagesDispatcher({
          type: messageActionTypes.FETCH_PREV_MESSAGES_SUCCESS,
          payload: { currentGroupChannel, messages },
        });
        cb([messages, null]);
      })
      .catch((error) => {
        logger.error('Channel: Fetching messages failed', error);
        messagesDispatcher({
          type: messageActionTypes.FETCH_PREV_MESSAGES_FAILURE,
          payload: { currentGroupChannel },
        });
        cb([null, error]);
      });
  }, [currentGroupChannel, oldestMessageTimeStamp, replyType]);
}

export default useScrollCallback;
import { useCallback } from 'react';
import { ReplyType } from '@sendbird/chat/message';

import * as messageActionTypes from '../dux/actionTypes';
import { NEXT_RESULT_SIZE } from '../const';

function useScrollDownCallback({
  currentGroupChannel,
  latestMessageTimeStamp,
  userFilledMessageListQuery,
  hasMoreNext,
  replyType,
}, {
  logger,
  messagesDispatcher,
  sdk,
}) {
  return useCallback((cb) => {
    if (!hasMoreNext) { return; }
    const { appInfo = {} } = sdk;
    const isReactionEnabled = appInfo.useReaction || false;
    const messageListParams = {
      nextResultSize: NEXT_RESULT_SIZE,
      isInclusive: true,
      includeReactions: isReactionEnabled,
    };
    messageListParams.includeMetaArray = true;
    if (replyType && (replyType === 'QUOTE_REPLY' || replyType === 'THREAD')) {
      messageListParams.includeThreadInfo = true;
      messageListParams.includeParentMessageInfo = true;
      messageListParams.replyType = ReplyType.ONLY_REPLY_TO_CHANNEL;
    }
    if (userFilledMessageListQuery) {
      Object.keys(userFilledMessageListQuery).forEach((key) => {
        messageListParams[key] = userFilledMessageListQuery[key];
      });
    }
    logger.info('Channel: Fetching later messages', { currentGroupChannel, userFilledMessageListQuery });

    currentGroupChannel.getMessagesByTimestamp(
      latestMessageTimeStamp || new Date().getTime(),
      messageListParams,
    )
      .then((messages) => {
        messagesDispatcher({
          type: messageActionTypes.FETCH_NEXT_MESSAGES_SUCCESS,
          payload: { currentGroupChannel, messages },
        });
        cb([messages, null]);
      })
      .catch((error) => {
        logger.error('Channel: Fetching later messages failed', error);
        messagesDispatcher({
          type: messageActionTypes.FETCH_NEXT_MESSAGES_FAILURE,
          payload: { currentGroupChannel },
        });
        cb([null, error]);
      });
  }, [currentGroupChannel, latestMessageTimeStamp, hasMoreNext, replyType]);
}

