export const RESET_CHANNEL_LIST = 'RESET_CHANNEL_LIST';
export const CREATE_CHANNEL = 'CREATE_CHANNEL';
export const USER_INVITED = 'USER_INVITED';
export const UNLOAD_CHANNELS = 'UNLOAD_CHANNELS';
export const SET_CHANNEL_LOADING = 'SET_CHANNEL_LOADING';
export const LEAVE_CHANNEL_SUCCESS = 'LEAVE_CHANNEL_SUCCESS';

export const SET_CURRENT_CHANNEL = 'SET_CURRENT_CHANNEL';

export const FETCH_CHANNELS_START = 'FETCH_CHANNELS_START';
export const FETCH_CHANNELS_SUCCESS = 'FETCH_CHANNELS_SUCCESS';
export const FETCH_CHANNELS_FAILURE = 'FETCH_CHANNELS_FAILURE';

export const INIT_CHANNELS_START = 'INIT_CHANNELS_START';
export const INIT_CHANNELS_SUCCESS = 'INIT_CHANNELS_SUCCESS';
export const INIT_CHANNELS_FAILURE = 'INIT_CHANNELS_FAILURE';

export const INVITE_MEMBERS_SUCESS = 'INVITE_MEMBERS_SUCESS';

export const ON_USER_JOINED = 'ON_USER_JOINED';
export const ON_CHANNEL_DELETED = 'ON_CHANNEL_DELETED';
export const ON_LAST_MESSAGE_UPDATED = 'ON_LAST_MESSAGE_UPDATED';
export const ON_USER_LEFT = 'ON_USER_LEFT';
export const ON_CHANNEL_CHANGED = 'ON_CHANNEL_CHANGED';
export const ON_CHANNEL_ARCHIVED = 'ON_CHANNEL_ARCHIVED';
export const ON_CHANNEL_FROZEN = 'ON_CHANNEL_FROZEN';
export const ON_CHANNEL_UNFROZEN = 'ON_CHANNEL_UNFROZEN';

export const ON_READ_RECEIPT_UPDATED = 'ON_READ_RECEIPT_UPDATED';
export const ON_DELIVERY_RECEIPT_UPDATED = 'ON_DELIVERY_RECEIPT_UPDATED';

export const CHANNEL_REPLACED_TO_TOP = 'CHANNEL_REPLACED_TO_TOP';
export const CHANNEL_LIST_PARAMS_UPDATED = 'CHANNEL_LIST_PARAMS_UPDATED';
import { GroupChannelHandler } from '@sendbird/chat/groupChannel';
import * as channelActions from './dux/actionTypes';
import topics from '../../lib/pubSub/topics';

const DELIVERY_RECIPT = 'delivery_receipt';

const createEventHandler = ({
  sdk,
  sdkChannelHandlerId,
  channelListDispatcher,
  logger,
}) => {
  const ChannelHandler = new GroupChannelHandler({
    onChannelChanged: (channel) => {
      logger.info('ChannelList: onChannelChanged', channel);
      channelListDispatcher({
        type: channelActions.ON_CHANNEL_CHANGED,
        payload: channel,
      });
    },
    onChannelDeleted: (channelUrl) => {
      logger.info('ChannelList: onChannelDeleted', channelUrl);
      channelListDispatcher({
        type: channelActions.ON_CHANNEL_DELETED,
        payload: channelUrl,
      });
    },
    onUserJoined: (channel) => {
      logger.info('ChannelList: onUserJoined', channel);
      if (channel?.lastMessage) {
        channelListDispatcher({
          type: channelActions.ON_USER_JOINED,
          payload: channel,
        });
      }
    },
    onUserReceivedInvitation: (channel, inviter, invitees) => {
      logger.info('ChannelList: onUserReceivedInvitation', { channel, inviter, invitees });
      const { currentUser } = sdk;
      const isInvited = invitees.find((user) => user?.userId === currentUser?.userId);
      // The newly created channel should be displayed only when there's a message
      if (isInvited && channel?.lastMessage) {
        channelListDispatcher({
          type: channelActions.USER_INVITED,
          payload: channel,
        });
      }
    },
    onUserBanned: (channel, user) => {
      const { currentUser } = sdk;
      logger.info('Channel: onUserBanned', channel);
      if (user.userId === currentUser.userId) {
        channelListDispatcher({
          type: channelActions.ON_USER_LEFT,
          payload: {
            channel,
            isMe: true,
          },
        });
      } else {
        channelListDispatcher({
          type: channelActions.ON_USER_LEFT,
          payload: {
            channel,
            isMe: false,
          },
        });
      }
    },
    onUserLeft: (channel, leftUser) => {
      const { currentUser } = sdk;
      const isMe = (currentUser.userId === leftUser.userId);
      logger.info('ChannelList: onUserLeft', channel);
      channelListDispatcher({
        type: channelActions.ON_USER_LEFT,
        payload: {
          channel,
          isMe,
        },
      });
    },

    onReadStatus: (channel) => {
      logger.info('ChannelList: onReadStatus', channel);
      channelListDispatcher({
        type: channelActions.ON_READ_RECEIPT_UPDATED,
        payload: channel,
      });
    },

    onDeliveryReceiptUpdated: (channel) => {
      logger.info('ChannelList: onDeliveryReceiptUpdated', channel);
      if (channel?.lastMessage) {
        channelListDispatcher({
          type: channelActions.ON_DELIVERY_RECEIPT_UPDATED,
          payload: channel,
        });
      }
    },

    onMessageUpdated: (channel, message) => {
      if (channel?.lastMessage.isEqual(message)) {
        logger.info('ChannelList: onMessageUpdated', channel);
        channelListDispatcher({
          type: channelActions.ON_LAST_MESSAGE_UPDATED,
          payload: channel,
        });
      }
    },

    onChannelHidden: (channel) => {
      logger.info('ChannelList: onChannelHidden', channel);
      channelListDispatcher({
        type: channelActions.ON_CHANNEL_ARCHIVED,
        payload: channel,
      });
    },

    onChannelFrozen: (channel) => {
      logger.info('ChannelList: onChannelFrozen', channel);
      channelListDispatcher({
        type: channelActions.ON_CHANNEL_FROZEN,
        payload: channel,
      });
    },

    onChannelUnfrozen: (channel) => {
      logger.info('ChannelList: onChannelUnfrozen', channel);
      channelListDispatcher({
        type: channelActions.ON_CHANNEL_UNFROZEN,
        payload: channel,
      });
    },
  });

  logger.info('ChannelList: Added channelHandler');
  sdk.groupChannel.addGroupChannelHandler(sdkChannelHandlerId, ChannelHandler);
};

const createChannelListQuery = ({ sdk, userFilledChannelListQuery = {} }) => {
  const param = {};
  param.includeEmpty = false;
  param.limit = 20; // The value of pagination limit could be set up to 100.
  param.order = 'latest_last_message'; // 'chronological', 'latest_last_message', 'channel_name_alphabetical', and 'metadata_value_alphabetical'

  if (userFilledChannelListQuery) {
    Object.keys(userFilledChannelListQuery).forEach((key) => {
      param[key] = userFilledChannelListQuery[key];
    });
  }

  const channelListQuery = sdk.groupChannel.createMyGroupChannelListQuery(param);

  return channelListQuery;
};

/**
 * Setup event listener
 * create channel source query
 * addloading screen
 */
function setupChannelList({
  sdk,
  sdkChannelHandlerId,
  channelListDispatcher,
  setChannelSource,
  onChannelSelect,
  userFilledChannelListQuery,
  logger,
  sortChannelList,
  disableAutoSelect,
  markAsDeliveredScheduler,
  disableMarkAsDelivered,
}) {
  if (sdk?.groupChannel) {
    createEventHandler({
      sdk,
      channelListDispatcher,
      sdkChannelHandlerId,
      logger,
    });
  } else {
    logger.warning('ChannelList - createEventHandler: sdk or sdk.ChannelHandler does not exist', sdk);
  }

  logger.info('ChannelList - creating query', { userFilledChannelListQuery });
  const channelListQuery = createChannelListQuery({ sdk, userFilledChannelListQuery });
  logger.info('ChannelList - created query', channelListQuery);
  setChannelSource(channelListQuery);

  channelListDispatcher({
    type: channelActions.INIT_CHANNELS_START,
  });

  if (userFilledChannelListQuery) {
    logger.info('ChannelList - setting up channelListQuery', channelListQuery);
    channelListDispatcher({
      type: channelActions.CHANNEL_LIST_PARAMS_UPDATED,
      payload: {
        channelListQuery,
        currentUserId: sdk && sdk.currentUser && sdk.currentUser.userId,
      },
    });
  }

  logger.info('ChannelList - fetching channels');
  if (channelListQuery.hasNext) {
    channelListQuery.next().then((channelList) => {
      logger.info('ChannelList - fetched channels', channelList);
      // select first channel
      logger.info('ChannelList - highlight channel', channelList[0]);
      let sortedChannelList = channelList;
      if (sortChannelList && typeof sortChannelList === 'function') {
        sortedChannelList = sortChannelList(channelList);
        logger.info('ChannelList - channel list sorted', sortedChannelList);
      }
      if (!disableAutoSelect) {
        onChannelSelect(sortedChannelList[0]);
      }
      channelListDispatcher({
        type: channelActions.INIT_CHANNELS_SUCCESS,
        payload: { channelList: sortedChannelList, disableAutoSelect },
      });
      const canSetMarkAsDelivered = sdk?.appInfo?.premiumFeatureList
        ?.find((feature) => (feature === DELIVERY_RECIPT));

      if (canSetMarkAsDelivered && !disableMarkAsDelivered) {
        sortedChannelList.forEach((channel) => {
          markAsDeliveredScheduler.push(channel);
        });
      }
    }).catch((err) => {
      if (err) {
        logger.error('ChannelList - couldnt fetch channels', err);
        channelListDispatcher({
          type: channelActions.INIT_CHANNELS_FAILURE,
        });
      }
    });
  } else {
    logger.info('ChannelList - there are no more channels');
  }
}

export const pubSubHandleRemover = (subscriber) => {
  subscriber.forEach((s) => {
    try {
      s.remove();
    } catch {
      //
    }
  });
};

export const pubSubHandler = (pubSub, channelListDispatcher) => {
  const subscriber = new Map();
  if (!pubSub) return subscriber;
  subscriber.set(topics.CREATE_CHANNEL, pubSub.subscribe(topics.CREATE_CHANNEL, (msg) => {
    const { channel } = msg;
    channelListDispatcher({
      type: 'CREATE_CHANNEL',
      payload: channel,
    });
  }));

  subscriber.set(topics.UPDATE_USER_MESSAGE, pubSub.subscribe(topics.UPDATE_USER_MESSAGE, (msg) => {
    const { channel, message } = msg;
    const updatedChannel = channel;
    if (updatedChannel?.lastMessage?.messageId === message.messageId) {
      updatedChannel.lastMessage = message;
    }
    if (channel) {
      channelListDispatcher({
        type: channelActions.ON_LAST_MESSAGE_UPDATED,
        payload: updatedChannel,
      });
    }
  }));

  subscriber.set(topics.LEAVE_CHANNEL, pubSub.subscribe(topics.LEAVE_CHANNEL, (msg) => {
    const { channel } = msg;
    channelListDispatcher({
      type: channelActions.LEAVE_CHANNEL_SUCCESS,
      payload: channel?.url,
    });
  }));

  subscriber.set(topics.SEND_MESSAGE_START, pubSub.subscribe(topics.SEND_MESSAGE_START, (msg) => {
    const { channel } = msg;
    channelListDispatcher({
      type: channelActions.CHANNEL_REPLACED_TO_TOP,
      payload: channel,
    });
  }));

  return subscriber;
};

export default setupChannelList;
export default {
  // we might not need this initialized state -> should remove
  initialized: false,
  loading: true,
  allChannels: [],
  currentChannel: null,
  channelListQuery: null,
  currentUserId: '',
  disableAutoSelect: false,
};
import { filterChannelListParams, getChannelsWithUpsertedChannel } from '../../../utils';
import * as actions from './actionTypes';
import initialState from './initialState';

export default function reducer(state, action) {
  switch (action.type) {
    case actions.INIT_CHANNELS_START:
      return {
        ...state,
        loading: true,
      };
    case actions.RESET_CHANNEL_LIST:
      return initialState;
    case actions.INIT_CHANNELS_SUCCESS: {
      const { channelList, disableAutoSelect } = action.payload;
      return {
        ...state,
        initialized: true,
        loading: false,
        allChannels: channelList,
        disableAutoSelect,
        currentChannel: (
          !disableAutoSelect
          && channelList
          && channelList.length
          && channelList.length > 0
        )
          ? channelList[0]
          : null,
      };
    }
    case actions.FETCH_CHANNELS_SUCCESS: {
      const currentChannels = state.allChannels.map((c) => c.url);
      const filteredChannels = action.payload.filter(
        ({ url }) => !(currentChannels.find((c) => c === url)),
      );
      return {
        ...state,
        allChannels: [
          ...state.allChannels,
          ...filteredChannels,
        ],
      };
    }
    case actions.USER_INVITED: {
      const channel = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(state.allChannels, channel),
          };
        }
        return {
          ...state,
          currentChannel: channel,
        };
      }
      return {
        ...state,
        allChannels: [channel, ...state.allChannels.filter((ch) => ch.url !== channel?.url)],
      };
    }
    case actions.CREATE_CHANNEL: {
      const channel = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(state.allChannels, channel),
          };
        }
        return {
          ...state,
          currentChannel: channel,
        };
      }
      return {
        ...state,
        allChannels: [channel, ...state.allChannels.filter((ch) => ch.url !== channel?.url)],
        currentChannel: channel,
      };
    }
    case actions.ON_CHANNEL_ARCHIVED: {
      const channel = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(state.allChannels, channel),
          };
          // TODO: Check if we have to set current channel
        }
      }
      const nextChannel = (channel?.url === state.currentChannel?.url)
        ? state.allChannels[state.allChannels[0].url === channel?.url ? 1 : 0]
        : state.currentChannel;
      return {
        ...state,
        allChannels: state.allChannels.filter(({ url }) => url !== channel?.url),
        currentChannel: state.disableAutoSelect ? null : nextChannel,
      };
    }
    case actions.LEAVE_CHANNEL_SUCCESS:
    case actions.ON_CHANNEL_DELETED: {
      const channelUrl = action.payload;
      return {
        ...state,
        currentChannel: (channelUrl === state.currentChannel?.url)
          ? state.allChannels[0]
          : state.currentChannel,
        allChannels: state.allChannels.filter(({ url }) => url !== channelUrl),
      };
    }
    case actions.ON_USER_LEFT: {
      const { channel, isMe } = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          const filteredChannels = getChannelsWithUpsertedChannel(state.allChannels, channel);
          const nextChannel = (isMe && (channel?.url === state.currentChannel?.url))
            ? filteredChannels[0]
            : state.currentChannel;
          return {
            ...state,
            currentChannel: state.disableAutoSelect ? null : nextChannel,
            allChannels: filteredChannels,
          };
        }
        const nextChannel = (channel?.url === state.currentChannel?.url)
          ? state.allChannels[0]
          : state.currentChannel;
        return {
          ...state,
          currentChannel: state.disableAutoSelect ? null : nextChannel,
          allChannels: state.allChannels.filter(({ url }) => url !== channel?.url),
        };
      }
      const filteredChannels = state.allChannels.filter((c) => !(c.url === channel?.url && isMe));
      const nextChannel = (isMe && (channel?.url === state.currentChannel?.url))
        ? filteredChannels[0]
        : state.currentChannel;
      return {
        ...state,
        currentChannel: state.disableAutoSelect ? null : nextChannel,
        allChannels: filteredChannels,
      };
    }
    case actions.ON_USER_JOINED:
    case actions.ON_CHANNEL_CHANGED:
    case actions.ON_READ_RECEIPT_UPDATED:
    case actions.ON_DELIVERY_RECEIPT_UPDATED: {
      const { allChannels = [] } = state;
      const channel = action.payload;
      const { unreadMessageCount } = channel;
      if (!channel?.lastMessage) return state;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(allChannels, channel),
          };
        }
        const nextChannel = (channel?.url === state.currentChannel?.url)
          ? state.allChannels[state.allChannels[0].url === channel?.url ? 1 : 0]
          // if coming channel is first of channel list, current channel will be the next one
          : state.currentChannel;
        return {
          ...state,
          currentChannel: state.disableAutoSelect ? null : nextChannel,
          allChannels: state.allChannels.filter(({ url }) => url !== channel?.url),
        };
      }
      // if its only an unread message count change, dont push to top
      if (unreadMessageCount === 0) {
        const currentChannel = allChannels.find(({ url }) => url === channel?.url);
        const currentUnreadCount = currentChannel && currentChannel.unreadMessageCount;
        if (currentUnreadCount === 0) {
          return {
            ...state,
            allChannels: state.allChannels.map((ch) => (ch.url === channel?.url ? channel : ch)),
          };
        }
      }
      return {
        ...state,
        allChannels: [
          channel,
          ...state.allChannels.filter(({ url }) => url !== action.payload.url),
        ],
      };
    }
    case actions.SET_CURRENT_CHANNEL:
      return {
        ...state,
        currentChannel: action.payload,
      };
    case actions.ON_LAST_MESSAGE_UPDATED: {
      return {
        ...state,
        allChannels: state.allChannels.map((channel) => (
          channel?.url === action.payload.url ? action.payload : channel)),
      };
    }
    case actions.ON_CHANNEL_FROZEN: {
      const channel = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(state.allChannels, channel),
          };
        }
        const nextChannel = (channel?.url === state.currentChannel?.url)
          ? state.allChannels[state.allChannels[0].url === channel?.url ? 1 : 0]
          // if coming channel is first of channel list, current channel will be the next one
          : state.currentChannel;
        return {
          ...state,
          allChannels: state.allChannels.filter(({ url }) => url !== channel?.url),
          currentChannel: state.disableAutoSelect ? null : nextChannel,
        };
      }
      return {
        ...state,
        allChannels: state.allChannels.map((ch) => {
          if (ch.url === channel?.url) {
            // eslint-disable-next-line no-param-reassign
            ch.isFrozen = true;
            return ch;
          }
          return ch;
        }),
      };
    }
    case actions.ON_CHANNEL_UNFROZEN: {
      const channel = action.payload;
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, channel, state.currentUserId)) {
          return {
            ...state,
            allChannels: getChannelsWithUpsertedChannel(state.allChannels, channel),
          };
        }
        const nextChannel = (channel?.url === state.currentChannel?.url)
          ? state.allChannels[state.allChannels[0].url === channel?.url ? 1 : 0]
          // if coming channel is first of channel list, current channel will be the next one
          : state.currentChannel;
        return {
          ...state,
          allChannels: state.allChannels.filter(({ url }) => url !== channel?.url),
          currentChannel: state.disableAutoSelect ? null : nextChannel,
        };
      }
      return {
        ...state,
        allChannels: state.allChannels.map((ch) => {
          if (ch.url === channel?.url) {
            // eslint-disable-next-line no-param-reassign
            ch.isFrozen = false;
            return ch;
          }
          return ch;
        }),
      };
    }
    case actions.CHANNEL_REPLACED_TO_TOP: {
      if (state.channelListQuery) {
        if (filterChannelListParams(state.channelListQuery, action.payload, state.currentUserId)) {
          return {
            ...state,
            allChannels: [
              action.payload,
              ...state.allChannels.filter((channel) => channel?.url !== action.payload.url),
            ],
          };
        }
        return state;
      }
      return {
        ...state,
        allChannels: [
          action.payload,
          ...state.allChannels.filter((channel) => channel?.url !== action.payload.url),
        ],
      };
    }
    case actions.CHANNEL_LIST_PARAMS_UPDATED:
      return {
        ...state,
        currentUserId: action.payload.currentUserId,
        channelListQuery: action.payload.channelListQuery,
      };
    default:
      return state;
  }
}
import { useEffect } from 'react';
import * as messageActionTypes from '../../dux/actionTypes';
import { GroupChannel, SendbirdGroupChat } from '@sendbird/chat/groupChannel';
import { Logger } from '../../../../lib/SendbirdState';

export type DynamicProps = {
  activeChannelUrl?: string;
  channels?: GroupChannel[];
  sdk?: SendbirdGroupChat;
};

export type StaticProps = {
  logger: Logger;
  channelListDispatcher: React.Dispatch<any>;
};

function useActiveChannelUrl({
  activeChannelUrl,
  channels,
  sdk,
}: DynamicProps, {
  logger,
  channelListDispatcher,
}: StaticProps): void {
  return useEffect(() => {
    if (activeChannelUrl) {
      logger.info('ChannelListProvider: looking for active channel', { activeChannelUrl });
      const activeChannel = channels?.find(channel => channel.url === activeChannelUrl);
      if (activeChannel) {
        channelListDispatcher({
          type: messageActionTypes.SET_CURRENT_CHANNEL,
          payload: activeChannel,
        });
      } else {
        logger.info('ChannelListProvider: searching backend for active channel', { activeChannelUrl });
        sdk?.groupChannel?.getChannel(activeChannelUrl)
          .then((channel) => {
            channelListDispatcher({
              type: messageActionTypes.FETCH_CHANNELS_SUCCESS,
              payload: [channel],
            });
            channelListDispatcher({
              type: messageActionTypes.SET_CURRENT_CHANNEL,
              payload: channel,
            });
          })
          .catch(() => {
            logger.warning('ChannelListProvider: Active channel not found');
          });
      }
    }
  }, [activeChannelUrl]);
}

export default useActiveChannelUrl;
import React, {
  useState,
  useEffect,
  useContext,
  useReducer,
  useMemo,
} from 'react';

import type { User } from '@sendbird/chat';
import {
  GroupChannel,
  GroupChannelCreateParams,
  GroupChannelHandler,
  SendbirdGroupChat,
  GroupChannelListQuery as GroupChannelListQuerySb,
  GroupChannelUserIdsFilter,
} from '@sendbird/chat/groupChannel';

import { RenderUserProfileProps } from '../../../types';

import setupChannelList, {
  pubSubHandler,
  pubSubHandleRemover,
} from '../utils';
import { uuidv4 } from '../../../utils/uuid';
import { noop } from '../../../utils/utils';

import * as channelListActions from '../dux/actionTypes';

import { UserProfileProvider } from '../../../lib/UserProfileContext';
import useSendbirdStateContext from '../../../hooks/useSendbirdStateContext';
import { CustomUseReducerDispatcher } from '../../../lib/SendbirdState';
import channelListReducers from '../dux/reducers';
import channelListInitialState from '../dux/initialState';
import { CHANNEL_TYPE } from '../../CreateChannel/types';
import useActiveChannelUrl from './hooks/useActiveChannelUrl';

interface ApplicationUserListQuery {
  limit?: number;
  userIdsFilter?: Array<string>;
  metaDataKeyFilter?: string;
  metaDataValuesFilter?: Array<string>;
}

interface GroupChannelListQuery {
  limit?: number;
  includeEmpty?: boolean;
  order?: 'latest_last_message' | 'chronological' | 'channel_name_alphabetical' | 'metadata_value_alphabetical';
  userIdsExactFilter?: Array<string>;
  userIdsIncludeFilter?: Array<string>;
  userIdsIncludeFilterQueryType?: 'AND' | 'OR';
  nicknameContainsFilter?: string;
  channelNameContainsFilter?: string;
  customTypesFilter?: Array<string>;
  customTypeStartsWithFilter?: string;
  channelUrlsFilter?: Array<string>;
  superChannelFilter?: 'all' | 'super' | 'nonsuper';
  publicChannelFilter?: 'all' | 'public' | 'private';
  metadataOrderKeyFilter?: string;
  memberStateFilter?: 'all' | 'joined_only' | 'invited_only' | 'invited_by_friend' | 'invited_by_non_friend';
  hiddenChannelFilter?: 'unhidden_only' | 'hidden_only' | 'hidden_allow_auto_unhide' | 'hidden_prevent_auto_unhide';
  unreadChannelFilter?: 'all' | 'unread_message';
  includeFrozen?: boolean;
  userIdsFilter?: GroupChannelUserIdsFilter;
}

interface ChannelListQueries {
  applicationUserListQuery?: ApplicationUserListQuery;
  channelListQuery?: GroupChannelListQuery;
}

type OverrideInviteUserType = {
  users: Array<string>;
  onClose: () => void;
  channelType: CHANNEL_TYPE;
};

export interface ChannelListProviderProps {
  allowProfileEdit?: boolean;
  onBeforeCreateChannel?(users: Array<string>): GroupChannelCreateParams;
  overrideInviteUser?(params: OverrideInviteUserType): void;
  onThemeChange?(theme: string): void;
  onProfileEditSuccess?(user: User): void;
  onChannelSelect?(channel: GroupChannel | null): void;
  sortChannelList?: (channels: GroupChannel[]) => GroupChannel[];
  queries?: ChannelListQueries;
  children?: React.ReactElement;
  className?: string | string[];
  renderUserProfile?: (props: RenderUserProfileProps) => React.ReactElement;
  disableUserProfile?: boolean;
  disableAutoSelect?: boolean;
  activeChannelUrl?: string;
  typingChannels?: Array<GroupChannel>;
  isTypingIndicatorEnabled?: boolean;
  isMessageReceiptStatusEnabled?: boolean;
}

export interface ChannelListProviderInterface extends ChannelListProviderProps {
  initialized: boolean;
  loading: boolean;
  allChannels: GroupChannel[];
  currentChannel: GroupChannel;
  channelListQuery: GroupChannelListQuery;
  currentUserId: string;
  channelListDispatcher: CustomUseReducerDispatcher;
  channelSource: GroupChannelListQuerySb | null;
}

interface ChannelListStoreInterface {
  initialized: boolean;
  loading: boolean;
  allChannels: GroupChannel[];
  currentChannel: GroupChannel;
  channelListQuery: GroupChannelListQuery;
  currentUserId: string;
  disableAutoSelect: boolean;
}

const ChannelListContext = React.createContext<ChannelListProviderInterface | null>({
  disableUserProfile: true,
  allowProfileEdit: true,
  onBeforeCreateChannel: null,
  onThemeChange: null,
  onProfileEditSuccess: null,
  onChannelSelect: null,
  queries: {},
  className: null,
  initialized: false,
  loading: false,
  allChannels: [],
  currentChannel: null,
  channelListQuery: {},
  currentUserId: null,
  channelListDispatcher: null,
  channelSource: null,
  typingChannels: [],
});

const ChannelListProvider: React.FC<ChannelListProviderProps> = (props: ChannelListProviderProps) => {
  // destruct props
  const {
    children,
    className,
    disableUserProfile,
    allowProfileEdit,
    queries,
    onProfileEditSuccess,
    onThemeChange,
    onBeforeCreateChannel,
    sortChannelList,
    overrideInviteUser,
    activeChannelUrl,
    isTypingIndicatorEnabled = null,
    isMessageReceiptStatusEnabled = null,
  } = props;
  // disable autoselect, if activeChannelUrl is provided
  // useActiveChannelUrl should be executed when activeChannelUrl is present
  const disableAutoSelect = props?.disableAutoSelect || !!activeChannelUrl;
  const onChannelSelect = props?.onChannelSelect || noop;
  // fetch store from <SendbirdProvider />
  const globalStore = useSendbirdStateContext();
  const { config, stores } = globalStore;
  const { sdkStore } = stores;
  const { pubSub, logger, onUserProfileMessage } = config;
  const {
    markAsDeliveredScheduler,
    disableMarkAsDelivered = false,
    isTypingIndicatorEnabledOnChannelList = false,
    isMessageReceiptStatusEnabledOnChannelList = false,
  } = config;
  const sdk = sdkStore?.sdk as SendbirdGroupChat;

  // derive some variables
  // enable if it is true atleast once(both are flase by default)
  const userDefinedDisableUserProfile = disableUserProfile || config?.disableUserProfile;
  const userDefinedRenderProfile = config?.renderUserProfile;
  const enableEditProfile = allowProfileEdit || config?.allowProfileEdit;

  const userFilledChannelListQuery = queries?.channelListQuery;
  const userFilledApplicationUserListQuery = queries?.applicationUserListQuery;

  const sdkIntialized = sdkStore?.initialized;

  const [channelListStore, channelListDispatcher] = useReducer(
    channelListReducers,
    channelListInitialState,
  ) as [ChannelListStoreInterface, CustomUseReducerDispatcher];
  const { currentChannel } = channelListStore;

  const [channelSource, setChannelSource] = useState<GroupChannelListQuerySb | null>(null);
  const [typingChannels, setTypingChannels] = useState<Array<GroupChannel>>([]);

  useEffect(() => {
    const subscriber = pubSubHandler(pubSub, channelListDispatcher);
    return () => {
      pubSubHandleRemover(subscriber);
    };
  }, [sdkIntialized]);

  useEffect(() => {
    const sdkChannelHandlerId = uuidv4();
    if (sdkIntialized) {
      logger.info('ChannelList: Setup channelHandlers');
      setupChannelList({
        sdk,
        sdkChannelHandlerId,
        channelListDispatcher,
        setChannelSource,
        onChannelSelect,
        userFilledChannelListQuery,
        logger,
        sortChannelList,
        disableAutoSelect,
        markAsDeliveredScheduler,
        disableMarkAsDelivered,
      });
    } else {
      logger.info('ChannelList: Removing channelHandlers');
      // remove previous channelHandlers
      if (sdk?.groupChannel?.removeGroupChannelHandler) {
        sdk.groupChannel.removeGroupChannelHandler(sdkChannelHandlerId);
      }
      // remove channelSource
      setChannelSource(null);
      // cleanup
      channelListDispatcher({
        type: channelListActions.RESET_CHANNEL_LIST,
        payload: null,
      });
    }
    return () => {
      logger.info('ChannelList: Removing channelHandlers');
      if (sdk?.groupChannel?.removeGroupChannelHandler) {
        sdk?.groupChannel?.removeGroupChannelHandler(sdkChannelHandlerId);
      }
    };
  }, [sdkIntialized, userFilledChannelListQuery, sortChannelList]);

  useEffect(() => {
    let typingHandlerId = '';
    if (sdk?.groupChannel?.addGroupChannelHandler) {
      typingHandlerId = uuidv4();
      const handler = new GroupChannelHandler({
        onTypingStatusUpdated: (channel) => {
          const typingMemberCount = channel?.getTypingUsers()?.length;
          const channelList = typingChannels.filter(ch => ch.url !== channel.url);
          if (typingMemberCount > 0) {
            setTypingChannels([...channelList, channel]);
          } else {
            setTypingChannels(channelList);
          }
        },
        onUnreadMemberStatusUpdated(channel) {
          channelListDispatcher({
            type: channelListActions.ON_LAST_MESSAGE_UPDATED,
            payload: channel,
          });
        },
        onUndeliveredMemberStatusUpdated(channel) {
          channelListDispatcher({
            type: channelListActions.ON_LAST_MESSAGE_UPDATED,
            payload: channel,
          });
        },
        onMessageUpdated(channel) {
          channelListDispatcher({
            type: channelListActions.ON_LAST_MESSAGE_UPDATED,
            payload: channel,
          });
          sdk.groupChannel.getChannelWithoutCache(channel.url)
            .then((ch) => {
              channelListDispatcher({
                type: channelListActions.ON_LAST_MESSAGE_UPDATED,
                payload: ch,
              });
            });
        },
        onMentionReceived(channel) {
          channelListDispatcher({
            type: channelListActions.ON_LAST_MESSAGE_UPDATED,
            payload: channel,
          });
          sdk.groupChannel.getChannelWithoutCache(channel.url)
            .then((ch) => {
              channelListDispatcher({
                type: channelListActions.ON_LAST_MESSAGE_UPDATED,
                payload: ch,
              });
            });
        },
      });
      sdk?.groupChannel?.addGroupChannelHandler(typingHandlerId, handler);
    }
    return () => {
      if (sdk?.groupChannel?.removeGroupChannelHandler && typingHandlerId !== '') {
        sdk.groupChannel.removeGroupChannelHandler(typingHandlerId);
      }
    };
  }, [sdk?.currentUser?.userId]);

  const queries_ = useMemo(() => {
    return {
      applicationUserListQuery: userFilledApplicationUserListQuery,
      channelListQuery: userFilledChannelListQuery,
    };
  }, [
    userFilledApplicationUserListQuery,
    userFilledChannelListQuery,
  ]);

  const { allChannels } = channelListStore;
  const sortedChannels = (sortChannelList && typeof sortChannelList === 'function')
    ? sortChannelList(allChannels)
    : allChannels;

  if (sortedChannels.length !== allChannels.length) {
    const warning = `ChannelList: You have removed/added extra channels on sortChannelList
      this could cause unexpected problems`;
    // eslint-disable-next-line no-console
    console.warn(warning, { before: allChannels, after: sortedChannels });
    logger.warning(warning, { before: allChannels, after: sortedChannels });
  }

  // Set current channel (by on_channel_selected event)
  useEffect(() => {
    if (!sdk || !sdk.groupChannel || !currentChannel || !currentChannel?.url) {
      return;
    }
    sdk.groupChannel.getChannel(currentChannel.url).then((groupChannel) => {
      if (groupChannel) {
        onChannelSelect(groupChannel);
      } else {
        onChannelSelect(null);
      }
    });
  }, [currentChannel?.url]);

  // Set active channel (by url)
  useActiveChannelUrl({
    activeChannelUrl,
    channels: sortedChannels,
    sdk,
  }, {
    logger,
    channelListDispatcher,
  });

  return (
    <ChannelListContext.Provider value={{
      className,
      disableUserProfile,
      queries: queries_,
      onProfileEditSuccess,
      onThemeChange,
      onBeforeCreateChannel,
      overrideInviteUser,
      onChannelSelect,
      sortChannelList,
      allowProfileEdit: enableEditProfile,
      channelListDispatcher,
      channelSource,
      ...channelListStore,
      allChannels: sortedChannels,
      typingChannels,
      isTypingIndicatorEnabled: (isTypingIndicatorEnabled !== null) ? isTypingIndicatorEnabled : isTypingIndicatorEnabledOnChannelList,
      isMessageReceiptStatusEnabled: (isMessageReceiptStatusEnabled !== null) ? isMessageReceiptStatusEnabled : isMessageReceiptStatusEnabledOnChannelList,
    }}>
      <UserProfileProvider
        disableUserProfile={userDefinedDisableUserProfile}
        renderUserProfile={userDefinedRenderProfile}
        onUserProfileMessage={onUserProfileMessage}
      >
        <div className={`sendbird-channel-list ${className}`}>
          {children}
        </div>
      </UserProfileProvider>
    </ChannelListContext.Provider>
  );
};

function useChannelListContext(): ChannelListProviderInterface {
  const context: ChannelListProviderInterface = useContext(ChannelListContext);
  return context;
}

export {
  ChannelListProvider,
  useChannelListContext,
};
