            '--sendbird-light-secondary-300': '#2EBA9F',
            '--sendbird-light-secondary-200': '#6FD6BE',
            '--sendbird-light-secondary-100': '#AEF2DC',

            '--sendbird-light-information-100': '#b2d9ff',

            '--sendbird-light-error-500': '#A30E2D',
