export default useTheme;
import SendbirdChat from '@sendbird/chat';
import { CreateAction } from '../../../utils/typeHelpers/reducers/createAction';

export const SDK_ACTIONS = {
  INIT_SDK: 'INIT_SDK',
  SET_SDK_LOADING: 'SET_SDK_LOADING',
  RESET_SDK: 'RESET_SDK',
  SDK_ERROR: 'SDK_ERROR',
} as const;

type SDK_PAYLOAD_TYPES = {
  [SDK_ACTIONS.SET_SDK_LOADING]: boolean,
  [SDK_ACTIONS.INIT_SDK]: SendbirdChat,
  [SDK_ACTIONS.SDK_ERROR]: null,
  [SDK_ACTIONS.RESET_SDK]: null,
};

export type SdkActionTypes = CreateAction<SDK_PAYLOAD_TYPES>;
import SendbirdChat from '@sendbird/chat';

export interface SdkStoreStateType {
  initialized: boolean;
  loading: boolean;
  sdk: SendbirdChat,
  error: boolean;
}

const initialState: SdkStoreStateType = {
  initialized: false,
  loading: false,
  sdk: {} as SendbirdChat,
  error: false,
};

export default initialState;
import { match } from 'ts-pattern';
import { SdkActionTypes, SDK_ACTIONS } from './actionTypes';
import initialState, { SdkStoreStateType } from './initialState';

export default function reducer(state: SdkStoreStateType, action: SdkActionTypes): SdkStoreStateType {
  return match(action)
    .with({ type: SDK_ACTIONS.SET_SDK_LOADING }, ({ payload }) => {
      return {
        ...state,
        initialized: false,
        loading: payload,
      };
    })
    .with({ type: SDK_ACTIONS.SDK_ERROR }, () => {
      return {
        ...state,
        initialized: false,
        loading: false,
        error: true,
      };
    })
    .with({ type: SDK_ACTIONS.INIT_SDK }, ({ payload }) => {
      return {
        sdk: payload,
        initialized: true,
        loading: false,
        error: false,
      };
    })
    .with({ type: SDK_ACTIONS.RESET_SDK }, () => {
      return initialState;
    })
    .otherwise(() => {
      return state;
    });
}
import { User } from '@sendbird/chat';

export interface UserStoreStateType {
  initialized: boolean;
  loading: boolean;
  user: User;
}

const initialState: UserStoreStateType = {
  initialized: false,
  loading: false,
  user: {} as User,
};

export default initialState;
import { match } from 'ts-pattern';
import { UserActionTypes, USER_ACTIONS } from './actionTypes';
import initialState, { UserStoreStateType } from './initialState';

export default function reducer(state: UserStoreStateType, action: UserActionTypes): UserStoreStateType {
  return match(action)
    .with({ type: USER_ACTIONS.INIT_USER }, ({ payload }) => {
      return {
        initialized: true,
        loading: false,
        user: payload,
      };
    })
    .with({ type: USER_ACTIONS.RESET_USER }, () => {
      return initialState;
    })
    .with({ type: USER_ACTIONS.UPDATE_USER_INFO }, ({ payload }) => {
      return {
        ...state,
        user: payload,
      };
    })
    .otherwise(() => {
      return state;
    });
}
import { ConnectionHandler } from '@sendbird/chat';
import { useState, useEffect } from 'react';

import { uuidv4 } from '../../utils/uuid';

function useConnectionStatus(sdk, logger) {
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    const uniqueHandlerId = uuidv4();
    try {
      logger.warning('sdk changed', uniqueHandlerId);
      const handler = new ConnectionHandler();
      handler.onDisconnected = () => {
        logger.warning('onDisconnected', { isOnline });
      };
      handler.onReconnectStarted = () => {
        setIsOnline(false);
        logger.warning('onReconnectStarted', { isOnline });
      };
      handler.onReconnectSucceeded = () => {
        setIsOnline(true);
        logger.warning('onReconnectSucceeded', { isOnline });
      };
      handler.onReconnectFailed = () => {
        sdk.reconnect();
        logger.warning('onReconnectFailed');
      };
      logger.info('Added ConnectionHandler', uniqueHandlerId);
      // workaround -> addConnectionHandler invalidates session handler
      // provided through configureSession
      if (sdk?.addConnectionHandler) {
        sdk.addConnectionHandler(uniqueHandlerId, handler);
      }
    } catch {
      //
    }
    return () => {
      try {
        sdk.removeConnectionHandler(uniqueHandlerId);
        logger.info('Removed ConnectionHandler', uniqueHandlerId);
      } catch {
        //
      }
    };
  }, [sdk]);

  useEffect(() => {
    const tryReconnect = () => {
      try {
        logger.warning('Try reconnecting SDK');
        if (sdk.connectionState !== 'OPEN') { // connection is not broken yet
          sdk.reconnect();
        }
      } catch {
        //
      }
    };
    // addEventListener version
    window.addEventListener('online', tryReconnect);
    return () => {
      window.removeEventListener('online', tryReconnect);
    };
  }, [sdk]);

  // add offline-class to body
  useEffect(() => {
    const body = document.querySelector('body');
    if (!isOnline) {
      try {
        body.classList.add('sendbird__offline');
        logger.info('Added class sendbird__offline to body');
      } catch (e) {
        //
      }
    } else {
      try {
        body.classList.remove('sendbird__offline');
        logger.info('Removed class sendbird__offline from body');
      } catch (e) {
        //
      }
    }
  }, [isOnline]);

  return isOnline;
}

export default useConnectionStatus;
import { SDK_ACTIONS } from '../../dux/sdk/actionTypes';
import { USER_ACTIONS } from '../../dux/user/actionTypes';
import { DisconnectSdkTypes } from './types';

export async function disconnectSdk({
  sdkDispatcher,
  userDispatcher,
  sdk,
}: DisconnectSdkTypes): Promise<boolean> {
  return new Promise((resolve) => {
    sdkDispatcher({ type: SDK_ACTIONS.SET_SDK_LOADING, payload: true });
    if (sdk?.disconnect) {
      sdk.disconnect()
        .then(() => {
          sdkDispatcher({ type: SDK_ACTIONS.RESET_SDK });
          userDispatcher({ type: USER_ACTIONS.RESET_USER });
        })
        .finally(() => {
          resolve(true);
        });
    } else {
      resolve(true);
    }
  });
}
import SendbirdChat, { SendbirdError, User } from '@sendbird/chat';
import { OpenChannelModule } from '@sendbird/chat/openChannel';
import { GroupChannelModule } from '@sendbird/chat/groupChannel';

import { SDK_ACTIONS } from '../../dux/sdk/actionTypes';
import { USER_ACTIONS } from '../../dux/user/actionTypes';

import { isTextuallyNull } from '../../../utils';

import { SetupConnectionTypes } from './types';

const APP_VERSION_STRING = '__react_dev_mode__';

const { INIT_SDK, SET_SDK_LOADING, RESET_SDK, SDK_ERROR } = SDK_ACTIONS;
const { INIT_USER, UPDATE_USER_INFO, RESET_USER } = USER_ACTIONS;

export function getMissingParamError({ userId, appId }: { userId?: string, appId?: string }): string {
  return `SendbirdProvider | useConnect/setupConnection/Connection failed UserId: ${userId} or appId: ${appId} missing`;
}
export function getConnectSbError(error?: SendbirdError): string {
  return `SendbirdProvider | useConnect/setupConnection/Connection failed. ${error?.code || ''} ${error?.message || ''}`;
}

export function setUpParams({
  appId,
  customApiHost,
  customWebSocketHost,
}: {
  appId: string;
  customApiHost?: string;
  customWebSocketHost?: string;
}): SendbirdChat {
  const params = {
    appId,
    modules: [
      new GroupChannelModule(),
      new OpenChannelModule(),
    ],
  };
  if (customApiHost) {
    params['customApiHost'] = customApiHost;
  }
  if (customWebSocketHost) {
    params['customWebSocketHost'] = customWebSocketHost;
  }
  const newSdk = SendbirdChat.init(params);
  return newSdk;
}

// Steps
// 1. Check if minimum userID/appID is provided
//  1.a. If not, throw error > !reject
//  1.b. If yes, continue
// 2. Set up params with custom host if provided
// 3. Set up session handler if provided
// 4. Set up version
// 5. Connect to Sendbird -> either using user ID or (user ID + access token)
// 6. If connected, connectCbSucess
//  6.a check if nickname is to be updated -> no > !resolve immediately
//  6.b check if nickname is to be updated -> yes > update nickname > !resolve
// 7. If not connected, connectCbError > !reject
export async function setUpConnection({
  logger,
  sdkDispatcher,
  userDispatcher,
  initDashboardConfigs,
  userId,
  appId,
  customApiHost,
  customWebSocketHost,
  configureSession,
  nickname,
  profileUrl,
  accessToken,
}: SetupConnectionTypes): Promise<void> {
  return new Promise((resolve, reject) => {
    logger?.info?.('SendbirdProvider | useConnect/setupConnection/init', { userId, appId });
    sdkDispatcher({ type: SET_SDK_LOADING, payload: true });

    if (userId && appId) {
      const newSdk = setUpParams({
        appId,
        customApiHost,
        customWebSocketHost,
      });

      if (configureSession && typeof configureSession === 'function') {
        const sessionHandler = configureSession(newSdk);
        logger?.info?.('SendbirdProvider | useConnect/setupConnection/configureSession', sessionHandler);
        newSdk.setSessionHandler(sessionHandler);
      }

      logger?.info?.('SendbirdProvider | useConnect/setupConnection/setVersion', { version: APP_VERSION_STRING });
      newSdk.addExtension('sb_uikit', APP_VERSION_STRING);

      const connectCbSucess = async (user: User) => {
        logger?.info?.('SendbirdProvider | useConnect/setupConnection/connectCbSucess', user);
        sdkDispatcher({ type: INIT_SDK, payload: newSdk });
        userDispatcher({ type: INIT_USER, payload: user });

        initDashboardConfigs(newSdk)
          .then(config => {
            logger?.info?.('SendbirdProvider | useConnect/setupConnection/getUIKitConfiguration success', {
              config,
            });
          })
          .catch(error => {
            logger?.error?.('SendbirdProvider | useConnect/setupConnection/getUIKitConfiguration failed', {
              error,
            });
          });

        // use nickname/profileUrl if provided
        // or set userID as nickname
        if ((nickname !== user.nickname || profileUrl !== user.profileUrl)
          && !(isTextuallyNull(nickname) && isTextuallyNull(profileUrl))
        ) {
          logger?.info?.('SendbirdProvider | useConnect/setupConnection/updateCurrentUserInfo', {
            nickname,
            profileUrl,
          });
          newSdk.updateCurrentUserInfo({
            nickname: nickname || user.nickname,
            profileUrl: profileUrl || user.profileUrl,
          }).then((namedUser) => {
            logger?.info?.('SendbirdProvider | useConnect/setupConnection/updateCurrentUserInfo success', {
              nickname,
              profileUrl,
            });
            userDispatcher({ type: UPDATE_USER_INFO, payload: namedUser });
          }).finally(() => {
            resolve();
          });
        } else {
          resolve();
        }
      };

      const connectCbError = (e: SendbirdError) => {
        const errorMessage = getConnectSbError(e);
        logger?.error?.(errorMessage, {
          e,
          appId,
          userId,
        });
        sdkDispatcher({ type: RESET_SDK });
        userDispatcher({ type: RESET_USER });

        sdkDispatcher({ type: SDK_ERROR });
        // exit promise with error
        reject(errorMessage);
      };

      logger?.info?.(`SendbirdProvider | useConnect/setupConnection/connect connecting using ${accessToken ?? userId}`);
      newSdk.connect(userId, accessToken)
        .then((res) => connectCbSucess(res))
        .catch((err) => connectCbError(err));
    } else {
      const errorMessage = getMissingParamError({ userId, appId });
      sdkDispatcher({ type: SDK_ERROR });
      logger?.error?.(errorMessage);
      // exit promise with error
      reject(errorMessage);
    }
  });
}
